import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [tab, setTab] = useState("school");
  // School registration
  const [schoolName, setSchoolName] = useState("");
  const [adminEmail, setAdminEmail] = useState("");
  const [adminPassword, setAdminPassword] = useState("");
  // User registration
  const [role, setRole] = useState("player");
  const [fullName, setFullName] = useState("");
  const [userEmail, setUserEmail] = useState("");
  const [userPassword, setUserPassword] = useState("");
  // Feedback
  const [err, setErr] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const handleSchoolRegister = async (e) => {
    e.preventDefault();
    setErr(""); setSuccess(""); setLoading(true);
    try {
      const url = process.env.REACT_APP_API_URL || "http://localhost:5000/api/school/register";
      const data = { school_name: schoolName, email: adminEmail, password: adminPassword };
      await axios.post(url, data);
      setSuccess("School registered! You can login now as School Admin.");
      setTimeout(() => navigate("/login"), 1300);
    } catch (error) {
      setErr(error?.response?.data?.message || "School registration failed.");
    }
    setLoading(false);
  };

  const handleUserRegister = async (e) => {
    e.preventDefault();
    setErr(""); setSuccess(""); setLoading(true);
    try {
      const url = process.env.REACT_APP_API_URL || "http://localhost:5000/api/auth/register";
      const data = { fullName, email: userEmail, password: userPassword, role };
      await axios.post(url, data);
      setSuccess("Registered! You can login now.");
      setTimeout(() => navigate("/login"), 1300);
    } catch (error) {
      setErr(error?.response?.data?.message || "Registration failed.");
    }
    setLoading(false);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-green-50">
      <div className="bg-white rounded-2xl shadow-xl w-full max-w-lg p-8">
        <div className="flex mb-8">
          <button
            className={`flex-1 py-2 font-semibold rounded-t-xl ${tab === "school"
              ? "bg-blue-600 text-white"
              : "bg-blue-100 text-blue-600"
              } transition`}
            onClick={() => setTab("school")}
          >
            School Registration
          </button>
          <button
            className={`flex-1 py-2 font-semibold rounded-t-xl ${tab === "user"
              ? "bg-green-600 text-white"
              : "bg-green-100 text-green-600"
              } transition`}
            onClick={() => setTab("user")}
          >
            User Registration
          </button>
        </div>

        {/* SCHOOL REGISTRATION */}
        {tab === "school" && (
          <form onSubmit={handleSchoolRegister}>
            <h2 className="text-xl font-bold mb-2 text-blue-700">Register Your School</h2>
            <div className="mb-3 text-sm text-blue-600">Creates a new school and admin account.</div>
            <input
              className="w-full border p-2 rounded mb-3"
              placeholder="School Name"
              value={schoolName}
              onChange={e => setSchoolName(e.target.value)}
              required
            />
            <input
              className="w-full border p-2 rounded mb-3"
              placeholder="Admin Email"
              type="email"
              value={adminEmail}
              onChange={e => setAdminEmail(e.target.value)}
              required
            />
            <input
              className="w-full border p-2 rounded mb-3"
              placeholder="Admin Password"
              type="password"
              value={adminPassword}
              onChange={e => setAdminPassword(e.target.value)}
              required
            />
            {err && <div className="text-red-500 mb-2">{err}</div>}
            {success && <div className="text-green-600 mb-2">{success}</div>}
            <button
              className="w-full py-2 bg-blue-600 text-white rounded font-semibold"
              type="submit"
              disabled={loading}
            >
              {loading ? "Registering..." : "Register School"}
            </button>
          </form>
        )}

        {/* USER REGISTRATION */}
        {tab === "user" && (
          <form onSubmit={handleUserRegister}>
            <h2 className="text-xl font-bold mb-2 text-green-700">Register as User</h2>
            <div className="mb-3 text-sm text-green-600">Coach, Referee, Player, or Organization.</div>
            <input
              className="w-full border p-2 rounded mb-3"
              placeholder="Full Name"
              value={fullName}
              onChange={e => setFullName(e.target.value)}
              required
            />
            <input
              className="w-full border p-2 rounded mb-3"
              placeholder="Email"
              type="email"
              value={userEmail}
              onChange={e => setUserEmail(e.target.value)}
              required
            />
            <input
              className="w-full border p-2 rounded mb-3"
              placeholder="Password"
              type="password"
              value={userPassword}
              onChange={e => setUserPassword(e.target.value)}
              required
            />
            <select
              className="w-full border p-2 rounded mb-3"
              value={role}
              onChange={e => setRole(e.target.value)}
              required
            >
              <option value="player">Player</option>
              <option value="coach">Coach</option>
              <option value="referee">Referee</option>
              <option value="organization">Organization</option>
              <option value="super_admin">Super Admin</option>
            </select>
            {err && <div className="text-red-500 mb-2">{err}</div>}
            {success && <div className="text-green-600 mb-2">{success}</div>}
            <button
              className="w-full py-2 bg-green-600 text-white rounded font-semibold"
              type="submit"
              disabled={loading}
            >
              {loading ? "Registering..." : "Register User"}
            </button>
          </form>
        )}

        <div className="mt-5 text-center text-sm">
          Already have an account?{" "}
          <span
            className="text-blue-600 underline cursor-pointer"
            onClick={() => navigate("/login")}
          >
            Login here
          </span>
        </div>
      </div>
    </div>
  );
}
