// src/api/playerApi.js
import axios from 'axios';
const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

export const registerPlayer = async (player, token) => {
  const response = await axios.post(`${API_BASE}/players/register`, player, {
    headers: { Authorization: `Bearer ${token}` }
  });
  return response.data;
};

export const uploadPlayerFiles = async (playerId, photo, birthCert, token) => {
  const formData = new FormData();
  if (photo) formData.append('photo', photo);
  if (birthCert) formData.append('birthCert', birthCert);

  const response = await axios.post(`${API_BASE}/players/upload/${playerId}`, formData, {
    headers: {
      'Content-Type': 'multipart/form-data',
      Authorization: `Bearer ${token}`
    }
  });
  return response.data;
};
