// src/components/Tournament/AddSportModal.jsx

import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { useState } from "react";
import sportsList from "@/data/sportsList"; // Not "export const", just "export default"
import * as Icons from "lucide-react";
import { motion } from "framer-motion";

export default function AddSportModal({ open, onClose, onAdd }) {
  const [search, setSearch] = useState("");
  const [selected, setSelected] = useState([]);

  // Filter sports by search query
  const filtered = sportsList.filter((s) =>
    s.name.toLowerCase().includes(search.toLowerCase())
  );

  // Add to selected (no duplicates)
  const handleSelect = (sport) => {
    if (!selected.find((s) => s.id === sport.id)) {
      setSelected([...selected, sport]);
    }
  };

  // Remove from selected
  const handleRemove = (id) => {
    setSelected(selected.filter((s) => s.id !== id));
  };

  // Icon helper (default to Trophy if not found)
  const getIcon = (iconName) => {
    const IconComp = Icons[iconName] || Icons.Trophy;
    return <IconComp className="w-8 h-8 text-athletiq-blue mb-2" />;
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogContent className="max-w-lg rounded-2xl">
        <DialogHeader>
          <DialogTitle>Select Sports</DialogTitle>
        </DialogHeader>

        <Input
          placeholder="Search sports..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="mb-4"
        />

        {/* Sports grid */}
        <div className="grid grid-cols-3 gap-4">
          {filtered.map((sport) => (
            <motion.div
              key={sport.id}
              whileHover={{ scale: 1.08 }}
              whileTap={{ scale: 0.97 }}
              className={`rounded-lg cursor-pointer bg-athletiq-cream shadow flex flex-col items-center p-3 border border-athletiq-blue ${
                selected.find((s) => s.id === sport.id)
                  ? "ring-2 ring-athletiq-green"
                  : ""
              }`}
              onClick={() => handleSelect(sport)}
              title={sport.name}
            >
              {getIcon(sport.icon)}
              <span className="font-semibold text-athletiq-navy text-sm">{sport.name}</span>
            </motion.div>
          ))}
        </div>

        {/* Selected list */}
        <div className="mt-6">
          <div className="font-bold text-athletiq-navy mb-2">Selected:</div>
          <div className="flex flex-wrap gap-2 min-h-[36px]">
            {selected.map((s) => (
              <div
                key={s.id}
                className="bg-athletiq-green text-white px-3 py-1 rounded-lg flex items-center gap-1"
              >
                <span className="font-medium">{s.name}</span>
                <button
                  className="text-white font-bold hover:text-red-300"
                  onClick={() => handleRemove(s.id)}
                  aria-label="Remove"
                >
                  âœ•
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Action */}
        <div className="flex justify-end mt-4">
          <button
            className="px-4 py-2 bg-athletiq-green text-white rounded font-semibold"
            onClick={() => {
              onAdd(selected);
              onClose();
              setSelected([]);
              setSearch("");
            }}
            disabled={selected.length === 0}
          >
            Add to Tournament
          </button>
        </div>
      </DialogContent>
    </Dialog>
  );
}
