// src/components/Sidebar.js

import React from "react";
import { Link, useLocation } from "react-router-dom";
import { FaUsers, FaUpload, FaUserPlus, FaSchool, FaTrophy, FaCog, FaSignOutAlt, FaHome } from "react-icons/fa";

// Import your logo (replace with correct path)
import athletiqLogo from "../assets/athletiq-logo.png"; // use your actual logo file

const navItems = [
  { label: "Dashboard", path: "/school-dashboard", icon: <FaHome /> },
  { label: "Player Roster", path: "/school-dashboard/roster", icon: <FaUsers /> },
  { label: "Add Player", path: "/school-dashboard/add", icon: <FaUserPlus /> },
  { label: "Bulk Upload", path: "/school-dashboard/bulk-upload", icon: <FaUpload /> },
  { label: "School Profile", path: "/school-dashboard/profile", icon: <FaSchool /> },
  { label: "Tournament Stats", path: "/school-dashboard/tournaments", icon: <FaTrophy /> },
  { label: "Settings", path: "/school-dashboard/settings", icon: <FaCog /> },
];

export default function Sidebar({ onLogout }) {
  const location = useLocation();

  return (
    <aside className="bg-athletiq-navy text-white min-h-screen w-60 flex flex-col px-0 pt-8 shadow-lg">
      <div className="flex flex-col items-center pb-8">
        <img src={athletiqLogo} alt="ATHLETIQ" className="h-12 mb-2" />
        <div className="text-xs tracking-wide opacity-60 mb-4">Track the Rise. Train the Future.</div>
      </div>
      <nav className="flex-1">
        {navItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center px-6 py-3 transition-all hover:bg-athletiq-blue
              ${location.pathname === item.path ? "bg-athletiq-blue font-semibold" : ""}
            `}
          >
            <span className={`mr-3 text-athletiq-green`}>{item.icon}</span>
            {item.label}
          </Link>
        ))}
      </nav>
      <button
        className="flex items-center px-6 py-3 mb-8 mt-auto text-athletiq-green hover:bg-athletiq-blue transition-all"
        onClick={onLogout}
      >
        <FaSignOutAlt className="mr-3" /> Logout
      </button>
    </aside>
  );
}
