// src/components/EditSchoolModal.js

import React, { useState, useRef, useEffect } from "react";
import axios from "axios";

export default function EditSchoolModal({ school, open, onClose, onUpdated }) {
  const [form, setForm] = useState({ ...school });
  const [logoFile, setLogoFile] = useState(null);
  const [docFile, setDocFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");

  const fileInputLogo = useRef();
  const fileInputDoc = useRef();

  // Reset form fields when modal is opened with a new school
  useEffect(() => {
    setForm({ ...school });
    setLogoFile(null);
    setDocFile(null);
    setErr("");
  }, [school, open]);

  if (!open) return null;

  const handleChange = e => {
    setForm(f => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleLogoChange = e => {
    setLogoFile(e.target.files[0]);
  };
  const handleDocChange = e => {
    setDocFile(e.target.files[0]);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setErr("");
    try {
      const data = new FormData();
      // All editable fields (add others as needed)
      [
        "name", "address", "email", "location_lat", "location_lng", "place_id", "is_active"
      ].forEach(key => {
        if (form[key] !== undefined && form[key] !== null) data.append(key, form[key]);
      });
      if (logoFile) data.append("logo", logoFile);
      if (docFile) data.append("registration_doc", docFile);

      const token = localStorage.getItem("token");
      const res = await axios.patch(
        `http://localhost:5000/api/schools/${school.id}`,
        data,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data"
          }
        }
      );
      onUpdated(res.data.school); // update parent state
      onClose();
    } catch (error) {
      setErr(error?.response?.data?.message || "Update failed.");
    }
    setLoading(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-30">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-xl relative">
        <button className="absolute top-2 right-3 text-lg" onClick={onClose}>&times;</button>
        <h2 className="text-xl font-bold mb-4">Edit School</h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <div>
            <label className="block text-sm mb-1">School Name</label>
            <input
              className="w-full border p-2 rounded"
              name="name"
              value={form.name || ""}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Address</label>
            <input
              className="w-full border p-2 rounded"
              name="address"
              value={form.address || ""}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Email</label>
            <input
              className="w-full border p-2 rounded"
              name="email"
              value={form.email || ""}
              onChange={handleChange}
              required
            />
          </div>
          <div className="flex gap-2">
            <div>
              <label className="block text-sm mb-1">Latitude</label>
              <input
                className="w-full border p-2 rounded"
                name="location_lat"
                value={form.location_lat || ""}
                onChange={handleChange}
                type="number"
                step="any"
              />
            </div>
            <div>
              <label className="block text-sm mb-1">Longitude</label>
              <input
                className="w-full border p-2 rounded"
                name="location_lng"
                value={form.location_lng || ""}
                onChange={handleChange}
                type="number"
                step="any"
              />
            </div>
            <div>
              <label className="block text-sm mb-1">Place ID</label>
              <input
                className="w-full border p-2 rounded"
                name="place_id"
                value={form.place_id || ""}
                onChange={handleChange}
              />
            </div>
          </div>
          <div>
            <label className="block text-sm mb-1">Active</label>
            <select
              className="w-full border p-2 rounded"
              name="is_active"
              value={form.is_active ? "true" : "false"}
              onChange={e => setForm(f => ({ ...f, is_active: e.target.value === "true" }))}
            >
              <option value="true">Yes</option>
              <option value="false">No</option>
            </select>
          </div>
          <div className="flex gap-4 items-center">
            <div>
              <label className="block text-sm mb-1">Logo</label>
              {school.logo_url && (
                <img
                  src={`http://localhost:5000/uploads/${school.logo_url}`}
                  alt="Logo"
                  className="h-16 mb-2 rounded shadow"
                />
              )}
              <input
                type="file"
                ref={fileInputLogo}
                onChange={handleLogoChange}
                accept="image/*"
              />
            </div>
            <div>
              <label className="block text-sm mb-1">Registration Doc</label>
              {school.registration_doc_url && (
                <a
                  href={`http://localhost:5000/uploads/${school.registration_doc_url}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-600 underline"
                >View Document</a>
              )}
              <input
                type="file"
                ref={fileInputDoc}
                onChange={handleDocChange}
                accept="application/pdf,image/*"
              />
            </div>
          </div>
          {err && <div className="text-red-500">{err}</div>}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white rounded py-2 mt-2"
            disabled={loading}
          >
            {loading ? "Updating..." : "Save Changes"}
          </button>
        </form>
      </div>
    </div>
  );
}
