// src/components/Tournament/AddSportsModal.js
import React, { useState } from "react";
import SportPicker from "./SportPicker";
import SportCard from "./SportCard";

/**
 * AddSportsModal
 * Props:
 * - open: boolean
 * - onClose: () => void
 * - onSave: (sportsListArray) => void
 */
export default function AddSportsModal({ open, onClose, onSave }) {
  const [selectedSports, setSelectedSports] = useState([]);

  // Add a new card (clone if same sport added multiple times)
  const addSport = sport => setSelectedSports(prev => [
    ...prev,
    { ...sport, gender: "", ageGroup: "", weightGroup: "", teamSize: "" }
  ]);
  // Remove a sport card
  const removeSport = idx => setSelectedSports(prev =>
    prev.filter((_, i) => i !== idx)
  );
  // Update a card
  const updateSport = (idx, fields) => setSelectedSports(prev => {
    const copy = [...prev];
    copy[idx] = { ...copy[idx], ...fields };
    return copy;
  });

  if (!open) return null;

  return (
    <div className="fixed inset-0 z-40 bg-black/30 flex items-center justify-center">
      <div className="bg-white rounded-xl shadow-2xl p-8 min-w-[360px] max-w-lg w-full relative">
        <button
          className="absolute right-4 top-3 text-2xl text-gray-500 hover:text-red-600"
          onClick={onClose}
        >Ã—</button>
        <h2 className="font-bold text-2xl text-athletiq-navy mb-3">Add Sports to Tournament</h2>

        <SportPicker onPick={addSport} />

        <div className="mt-5">
          <div className="font-semibold mb-2 text-athletiq-navy">Selected Sports</div>
          {selectedSports.length === 0 && (
            <div className="text-xs text-gray-500 mb-3">No sports selected yet.</div>
          )}
          <div>
            {selectedSports.map((s, i) =>
              <SportCard
                key={i}
                sport={s}
                onChange={fields => updateSport(i, fields)}
                onDelete={() => removeSport(i)}
              />
            )}
          </div>
        </div>

        <div className="mt-6 flex justify-end gap-2">
          <button className="px-4 py-2 bg-gray-200 rounded" onClick={onClose}>Cancel</button>
          <button
            className="px-4 py-2 bg-athletiq-green text-white rounded"
            onClick={() => { onSave(selectedSports); onClose(); }}
            disabled={selectedSports.length === 0}
          >Save Sports</button>
        </div>
      </div>
    </div>
  );
}
