// src/pages/AdminDashboard.js

import React, { useEffect, useState } from "react";
import axios from "axios";

// Simple modal component (uses absolute positioning)
function Modal({ open, onClose, children }) {
  if (!open) return null;
  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
      <div className="bg-white rounded shadow-lg p-6 min-w-[300px] relative">
        <button
          className="absolute top-2 right-2 text-gray-400 hover:text-gray-600"
          onClick={onClose}
          aria-label="Close"
        >
          ✖
        </button>
        {children}
      </div>
    </div>
  );
}

export default function AdminDashboard() {
  const [schools, setSchools] = useState([]);
  const [players, setPlayers] = useState([]);
  const [view, setView] = useState("schools");
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ schools: 0, players: 0 });
  const [err, setErr] = useState("");

  // Modal/editing state
  const [editSchool, setEditSchool] = useState(null);
  const [editPlayer, setEditPlayer] = useState(null);
  const [modalErr, setModalErr] = useState("");
  const [modalSuccess, setModalSuccess] = useState("");

  const token = localStorage.getItem("token");

  // Fetch all schools and players
  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      setErr("");
      try {
        const [schoolsRes, playersRes] = await Promise.all([
          axios.get("http://localhost:5000/api/schools", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get("http://localhost:5000/api/players", {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);
        setSchools(schoolsRes.data.schools || []);
        setPlayers(playersRes.data.players || []);
        setStats({
          schools: schoolsRes.data.schools?.length || 0,
          players: playersRes.data.players?.length || 0,
        });
      } catch (error) {
        setErr(error?.response?.data?.message || "Error loading data.");
      }
      setLoading(false);
    }
    fetchData();
  }, [token]);

  // School Edit Save
  async function handleSaveSchool() {
    setModalErr(""); setModalSuccess("");
    if (!editSchool) return;
    try {
      const resp = await axios.patch(
        `http://localhost:5000/api/schools/${editSchool.id}`,
        {
          name: editSchool.name,
          address: editSchool.address,
          email: editSchool.email,
          is_active: editSchool.is_active,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setModalSuccess("School updated!");
      // Update list
      setSchools(schools =>
        schools.map(s => (s.id === editSchool.id ? { ...s, ...resp.data.school } : s))
      );
      setTimeout(() => setEditSchool(null), 1000); // close modal after 1s
    } catch (error) {
      setModalErr(error?.response?.data?.message || "Update failed.");
    }
  }

  // Player Edit Save
  async function handleSavePlayer() {
    setModalErr(""); setModalSuccess("");
    if (!editPlayer) return;
    try {
      const resp = await axios.patch(
        `http://localhost:5000/api/players/${editPlayer.id}`,
        {
          full_name: editPlayer.full_name,
          dob: editPlayer.dob,
          guardian_phone: editPlayer.guardian_phone,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setModalSuccess("Player updated!");
      // Update list
      setPlayers(players =>
        players.map(p => (p.id === editPlayer.id ? { ...p, ...resp.data.player } : p))
      );
      setTimeout(() => setEditPlayer(null), 1000); // close modal after 1s
    } catch (error) {
      setModalErr(error?.response?.data?.message || "Update failed.");
    }
  }

  // Delete school
  async function handleDeleteSchool(id) {
    if (!window.confirm("Delete this school? This cannot be undone.")) return;
    try {
      await axios.delete(`http://localhost:5000/api/schools/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSchools(schools => schools.filter(s => s.id !== id));
    } catch (error) {
      alert(error?.response?.data?.message || "Delete failed.");
    }
  }

  // Delete player
  async function handleDeletePlayer(id) {
    if (!window.confirm("Delete this player?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/players/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPlayers(players => players.filter(p => p.id !== id));
    } catch (error) {
      alert(error?.response?.data?.message || "Delete failed.");
    }
  }

  return (
    <div className="p-8 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Super Admin Dashboard</h1>
      <div className="flex gap-6 mb-6">
        <button
          onClick={() => setView("schools")}
          className={`px-4 py-2 rounded ${view === "schools" ? "bg-blue-600 text-white" : "bg-blue-100"}`}
        >
          Schools
        </button>
        <button
          onClick={() => setView("players")}
          className={`px-4 py-2 rounded ${view === "players" ? "bg-blue-600 text-white" : "bg-blue-100"}`}
        >
          Players
        </button>
        <button
          onClick={() => setView("stats")}
          className={`px-4 py-2 rounded ${view === "stats" ? "bg-blue-600 text-white" : "bg-blue-100"}`}
        >
          Stats
        </button>
      </div>
      {err && <div className="text-red-500 mb-4">{err}</div>}
      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          {view === "schools" && (
            <div>
              <h2 className="text-xl font-semibold mb-3">All Schools</h2>
              <table className="min-w-full bg-white rounded shadow">
                <thead>
                  <tr className="bg-blue-100">
                    <th className="p-2 text-left">Name</th>
                    <th className="p-2 text-left">Address</th>
                    <th className="p-2 text-left">Email</th>
                    <th className="p-2 text-left">Active</th>
                    <th className="p-2 text-left">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {schools.map((s) => (
                    <tr key={s.id}>
                      <td className="p-2">{s.name}</td>
                      <td className="p-2">{s.address}</td>
                      <td className="p-2">{s.email}</td>
                      <td className="p-2">{s.is_active ? "Yes" : "No"}</td>
                      <td className="p-2">
                        <button
                          className="bg-green-500 text-white px-3 py-1 rounded mr-2"
                          onClick={() => {
                            setEditSchool(s);
                            setModalErr(""); setModalSuccess("");
                          }}
                        >
                          Edit
                        </button>
                        <button
                          className="bg-red-500 text-white px-3 py-1 rounded"
                          onClick={() => handleDeleteSchool(s.id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {view === "players" && (
            <div>
              <h2 className="text-xl font-semibold mb-3">All Players</h2>
              <table className="min-w-full bg-white rounded shadow">
                <thead>
                  <tr className="bg-blue-100">
                    <th className="p-2 text-left">Name</th>
                    <th className="p-2 text-left">School</th>
                    <th className="p-2 text-left">DOB</th>
                    <th className="p-2 text-left">Phone</th>
                    <th className="p-2 text-left">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {players.map((p) => (
                    <tr key={p.id}>
                      <td className="p-2">{p.full_name}</td>
                      <td className="p-2">{p.school_id}</td>
                      <td className="p-2">{p.dob}</td>
                      <td className="p-2">{p.guardian_phone}</td>
                      <td className="p-2">
                        <button
                          className="bg-green-500 text-white px-3 py-1 rounded mr-2"
                          onClick={() => {
                            setEditPlayer(p);
                            setModalErr(""); setModalSuccess("");
                          }}
                        >
                          Edit
                        </button>
                        <button
                          className="bg-red-500 text-white px-3 py-1 rounded"
                          onClick={() => handleDeletePlayer(p.id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {view === "stats" && (
            <div>
              <h2 className="text-xl font-semibold mb-4">Platform Stats</h2>
              <div className="flex gap-8">
                <div className="bg-blue-200 px-6 py-4 rounded text-center shadow">
                  <div className="text-3xl font-bold">{stats.schools}</div>
                  <div>Schools</div>
                </div>
                <div className="bg-green-200 px-6 py-4 rounded text-center shadow">
                  <div className="text-3xl font-bold">{stats.players}</div>
                  <div>Players</div>
                </div>
              </div>
            </div>
          )}

          {/* School Edit Modal */}
          <Modal open={!!editSchool} onClose={() => setEditSchool(null)}>
            {editSchool && (
              <form
                onSubmit={e => {
                  e.preventDefault();
                  handleSaveSchool();
                }}
                className="space-y-3"
              >
                <h3 className="font-bold text-lg mb-2">Edit School</h3>
                <div>
                  <label className="block mb-1">Name</label>
                  <input
                    className="w-full border p-2 rounded"
                    value={editSchool.name}
                    onChange={e => setEditSchool({ ...editSchool, name: e.target.value })}
                  />
                </div>
                <div>
                  <label className="block mb-1">Address</label>
                  <input
                    className="w-full border p-2 rounded"
                    value={editSchool.address}
                    onChange={e => setEditSchool({ ...editSchool, address: e.target.value })}
                  />
                </div>
                <div>
                  <label className="block mb-1">Email</label>
                  <input
                    className="w-full border p-2 rounded"
                    value={editSchool.email}
                    onChange={e => setEditSchool({ ...editSchool, email: e.target.value })}
                  />
                </div>
                <div>
                  <label className="block mb-1">Active</label>
                  <select
                    className="w-full border p-2 rounded"
                    value={editSchool.is_active ? "true" : "false"}
                    onChange={e => setEditSchool({ ...editSchool, is_active: e.target.value === "true" })}
                  >
                    <option value="true">Yes</option>
                    <option value="false">No</option>
                  </select>
                </div>
                {modalErr && <div className="text-red-500">{modalErr}</div>}
                {modalSuccess && <div className="text-green-500">{modalSuccess}</div>}
                <button className="mt-2 px-4 py-2 bg-blue-600 text-white rounded" type="submit">
                  Save Changes
                </button>
              </form>
            )}
          </Modal>

          {/* Player Edit Modal */}
          <Modal open={!!editPlayer} onClose={() => setEditPlayer(null)}>
            {editPlayer && (
              <form
                onSubmit={e => {
                  e.preventDefault();
                  handleSavePlayer();
                }}
                className="space-y-3"
              >
                <h3 className="font-bold text-lg mb-2">Edit Player</h3>
                <div>
                  <label className="block mb-1">Name</label>
                  <input
                    className="w-full border p-2 rounded"
                    value={editPlayer.full_name}
                    onChange={e => setEditPlayer({ ...editPlayer, full_name: e.target.value })}
                  />
                </div>
                <div>
                  <label className="block mb-1">DOB</label>
                  <input
                    className="w-full border p-2 rounded"
                    value={editPlayer.dob}
                    onChange={e => setEditPlayer({ ...editPlayer, dob: e.target.value })}
                  />
                </div>
                <div>
                  <label className="block mb-1">Guardian Phone</label>
                  <input
                    className="w-full border p-2 rounded"
                    value={editPlayer.guardian_phone}
                    onChange={e => setEditPlayer({ ...editPlayer, guardian_phone: e.target.value })}
                  />
                </div>
                {modalErr && <div className="text-red-500">{modalErr}</div>}
                {modalSuccess && <div className="text-green-500">{modalSuccess}</div>}
                <button className="mt-2 px-4 py-2 bg-blue-600 text-white rounded" type="submit">
                  Save Changes
                </button>
              </form>
            )}
          </Modal>
        </>
      )}
    </div>
  );
}
