import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../api/axios"; // <-- Make sure this is your axios helper

const Login = () => {
  const [identifier, setIdentifier] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Handle login form submission
  const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous error

    try {
      // POST to backend login endpoint
      const res = await API.post("/auth/login", {
        identifier,
        password,
      });
      const { token, user } = res.data;

      // Store token & user in localStorage
      localStorage.setItem("token", token);
      localStorage.setItem("user", JSON.stringify(user));

      // Redirect based on role
      if (user.role === "super_admin") navigate("/admin");
      else if (user.role === "school_admin") navigate("/dashboard");
      else if (user.role === "player") navigate(`/player/${user.id}`); // Player-specific page
      else navigate("/");

    } catch (err) {
      setError(
        err.response?.data?.message ||
        "Login failed. Please check your credentials."
      );
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form
        className="bg-white shadow-lg rounded-xl p-8 w-full max-w-md"
        onSubmit={handleLogin}
      >
        <h2 className="text-2xl font-bold mb-6 text-center">ATHLETIQ Login</h2>
        <label className="block mb-2 text-sm font-semibold">Email or Phone</label>
        <input
          className="mb-4 p-2 border rounded w-full"
          type="text"
          value={identifier}
          onChange={(e) => setIdentifier(e.target.value)}
          placeholder="Enter email or phone"
          required
        />
        <label className="block mb-2 text-sm font-semibold">Password</label>
        <input
          className="mb-6 p-2 border rounded w-full"
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Enter password"
          required
        />
        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-lg transition"
        >
          Login
        </button>
        {error && (
          <div className="mt-4 text-red-600 text-center font-medium">{error}</div>
        )}
      </form>
    </div>
  );
};

export default Login;
