// src/components/AdminDashboard/TournamentsTab.js

import React, { useState } from "react";
import AddTournamentModal from "./AddTournamentModal";

/**
 * TournamentsTab - Shows tournament list and handles add tournament modal.
 * Props:
 *   - tournaments: array
 *   - user: current user object
 *   - onAdded: function(newTournament) => void, for reloading or appending list
 */
export default function TournamentsTab({ tournaments, user, onAdded }) {
  const [addOpen, setAddOpen] = useState(false);

  return (
    <div>
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-bold text-athletiq-navy">All Tournaments</h2>
        <button
          onClick={() => setAddOpen(true)}
          className="px-4 py-2 bg-athletiq-green text-white rounded font-semibold hover:bg-green-700"
        >
          + Add Tournament
        </button>
      </div>
      {tournaments.length === 0 ? (
        <div>No tournaments found.</div>
      ) : (
        <div className="overflow-x-auto bg-white rounded-lg shadow-md">
          <table className="min-w-full text-sm">
            <thead>
              <tr className="bg-athletiq-blue text-white">
                <th className="p-2 text-left">Name</th>
                <th className="p-2 text-left">Code</th>
                <th className="p-2 text-left">Sports</th>
                <th className="p-2 text-left">Start Date</th>
                <th className="p-2 text-left">Status</th>
                <th className="p-2 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {tournaments.map((t) => (
                <tr key={t.id} className="border-b">
                  <td className="p-2">{t.name}</td>
                  <td className="p-2">{t.code}</td>
                  <td className="p-2">{Array.isArray(t.sports) ? t.sports.join(", ") : JSON.parse(t.sports || "[]").join(", ")}</td>
                  <td className="p-2">{t.start_date?.slice(0, 10)}</td>
                  <td className="p-2">{t.status || "Active"}</td>
                  <td className="p-2 flex gap-2">
                    {/* Edit/Delete buttons can go here */}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Add Tournament Modal */}
      <AddTournamentModal
        open={addOpen}
        onClose={() => setAddOpen(false)}
        user={user}
        onAdded={onAdded}
      />
    </div>
  );
}
