// src/pages/TournamentsPage.jsx

import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

/**
 * Super Admin Tournament Management Page
 */
export default function TournamentsPage() {
  const [tournaments, setTournaments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [showAdd, setShowAdd] = useState(false);

  const [form, setForm] = useState({
    name: "",
    sports: "",
    team_size: "",
    start_date: "",
    duration_days: "",
    logo_url: "",
    description: "",
    level: "national"
  });

  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    fetchTournaments();
    // eslint-disable-next-line
  }, []);

  async function fetchTournaments() {
    setLoading(true);
    setErr("");
    try {
      const res = await axios.get("http://localhost:5000/api/tournaments", {
        headers: { Authorization: `Bearer ${token}` }
      });
      setTournaments(Array.isArray(res.data) ? res.data : []);
    } catch (error) {
      if (error?.response?.status === 401) {
        setErr("Session expired. Redirecting to login...");
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        setTimeout(() => navigate("/login"), 1500);
      } else {
        setErr(error?.response?.data?.error || "Failed to load tournaments.");
      }
    }
    setLoading(false);
  }

  async function handleAdd(e) {
    e.preventDefault();
    setErr("");
    try {
      const sportsArr = form.sports.split(",").map(s => s.trim()).filter(Boolean);
      const payload = { ...form, sports: sportsArr };
      const res = await axios.post("http://localhost:5000/api/tournaments", payload, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const tournamentId = res.data.id;
      navigate(`/superadmin/tournaments/${tournamentId}/setup`);
    } catch (error) {
      if (error?.response?.status === 401) {
        setErr("Session expired. Redirecting to login...");
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        setTimeout(() => navigate("/login"), 1500);
      } else {
        setErr(error?.response?.data?.error || "Failed to add tournament.");
      }
    }
  }

  function handleManageClick(tournamentId) {
    try {
      if (!token) throw new Error("Missing token");
      navigate(`/superadmin/tournaments/${tournamentId}/setup`);
    } catch (error) {
      console.error("Error navigating to setup page:", error);
      setErr("Session expired. Please log in again.");
      localStorage.removeItem("token");
      localStorage.removeItem("user");
      setTimeout(() => navigate("/login"), 1500);
    }
  }

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Tournaments</h1>
      {err && <div className="text-red-500 mb-4">{err}</div>}

      <button
        className="px-4 py-2 mb-4 bg-athletiq-green text-white rounded"
        onClick={() => setShowAdd(!showAdd)}
      >
        {showAdd ? "Cancel" : "+ Add Tournament"}
      </button>

      {showAdd && (
        <form onSubmit={handleAdd} className="bg-white p-4 rounded shadow mb-6 grid gap-4">
          <div>
            <label className="block font-semibold">Name</label>
            <input className="border rounded w-full p-2"
              value={form.name} required
              onChange={e => setForm(f => ({ ...f, name: e.target.value }))}
            />
          </div>
          <div>
            <label className="block font-semibold">Sports (comma separated)</label>
            <input className="border rounded w-full p-2"
              value={form.sports}
              onChange={e => setForm(f => ({ ...f, sports: e.target.value }))}
              placeholder="e.g. football, cricket"
              required
            />
          </div>
          <div>
            <label className="block font-semibold">Team Size</label>
            <input className="border rounded w-full p-2"
              type="number"
              value={form.team_size}
              onChange={e => setForm(f => ({ ...f, team_size: e.target.value }))}
            />
          </div>
          <div>
            <label className="block font-semibold">Start Date</label>
            <input className="border rounded w-full p-2"
              type="date"
              value={form.start_date}
              onChange={e => setForm(f => ({ ...f, start_date: e.target.value }))}
              required
            />
          </div>
          <div>
            <label className="block font-semibold">Duration (days)</label>
            <input className="border rounded w-full p-2"
              type="number"
              value={form.duration_days}
              onChange={e => setForm(f => ({ ...f, duration_days: e.target.value }))}
            />
          </div>
          <div>
            <label className="block font-semibold">Level</label>
            <select className="border rounded w-full p-2"
              value={form.level}
              onChange={e => setForm(f => ({ ...f, level: e.target.value }))}
            >
              <option value="national">National</option>
              <option value="provincial">Provincial</option>
              <option value="district">District</option>
            </select>
          </div>
          <div>
            <label className="block font-semibold">Logo URL</label>
            <input className="border rounded w-full p-2"
              value={form.logo_url}
              onChange={e => setForm(f => ({ ...f, logo_url: e.target.value }))}
              placeholder="optional"
            />
          </div>
          <div>
            <label className="block font-semibold">Description</label>
            <textarea className="border rounded w-full p-2"
              value={form.description}
              onChange={e => setForm(f => ({ ...f, description: e.target.value }))}
            />
          </div>
          <button type="submit" className="bg-athletiq-blue text-white px-4 py-2 rounded">
            Create Tournament
          </button>
        </form>
      )}

      {loading ? (
        <div>Loading tournaments...</div>
      ) : tournaments.length === 0 ? (
        <div>No tournaments found.</div>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm border border-gray-200 bg-white">
            <thead>
              <tr className="bg-athletiq-blue text-white">
                <th className="p-2 text-left">Code</th>
                <th className="p-2 text-left">Name</th>
                <th className="p-2 text-left">Sports</th>
                <th className="p-2 text-left">Team Size</th>
                <th className="p-2 text-left">Start Date</th>
                <th className="p-2 text-left">Level</th>
                <th className="p-2 text-left">Status</th>
                <th className="p-2 text-left">Action</th>
              </tr>
            </thead>
            <tbody>
              {tournaments.map((t) => (
                <tr key={t.id}>
                  <td className="p-2">{t.code || t.tournament_code}</td>
                  <td className="p-2">{t.name}</td>
                  <td className="p-2">{Array.isArray(t.sports) ? t.sports.join(", ") : t.sports}</td>
                  <td className="p-2">{t.team_size}</td>
                  <td className="p-2">{t.start_date?.slice(0, 10)}</td>
                  <td className="p-2">{t.level}</td>
                  <td className="p-2">{t.status}</td>
                  <td className="p-2">
                    <button
                      onClick={() => handleManageClick(t.id)}
                      className="text-blue-600 hover:underline"
                    >
                      Manage
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
