// src/components/Tournament/BracketPreview.jsx

import React, { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { ChevronDown, ChevronRight, Trophy, Users } from "lucide-react";

export default function BracketPreview({ sports = [] }) {
  const [expanded, setExpanded] = useState({});

  const toggleExpand = (sportId) => {
    setExpanded((prev) => ({ ...prev, [sportId]: !prev[sportId] }));
  };

  return (
    <div className="w-full">
      <h2 className="text-xl font-bold text-athletiq-navy mb-4">
        Bracket Previews by Sport
      </h2>
      <div className="flex flex-col gap-4">
        {sports.map((sport) => (
          <Card
            key={sport.id}
            className="bg-athletiq-cream border border-athletiq-blue rounded-xl"
          >
            <CardContent className="p-4">
              <div
                className="flex justify-between items-center cursor-pointer"
                onClick={() => toggleExpand(sport.id)}
              >
                <h3 className="text-athletiq-navy font-semibold text-md">
                  {sport.name} ({sport.format?.toUpperCase() || "Format Unknown"})
                </h3>
                {expanded[sport.id] ? <ChevronDown /> : <ChevronRight />}
              </div>

              {expanded[sport.id] && (
                <div className="mt-4 flex gap-4 overflow-x-auto">
                  {getBracketRounds(sport.totalTeams || 8).map((round, index) => (
                    <div
                      key={index}
                      className="min-w-[160px] flex flex-col items-center p-3 bg-white rounded-lg shadow"
                    >
                      <h4 className="text-sm font-medium text-athletiq-navy mb-2">
                        Round {index + 1}
                      </h4>
                      <Separator className="mb-2 bg-athletiq-blue" />
                      {round.map((match, i) => (
                        <div
                          key={i}
                          className="rounded bg-athletiq-cream p-2 mb-2 w-full text-center text-sm font-semibold text-athletiq-blue"
                        >
                          Match {match.code}
                        </div>
                      ))}
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}

function getBracketRounds(totalTeams) {
  const rounds = [];
  let matches = Math.ceil(totalTeams / 2);
  let matchId = 1;
  while (matches >= 1) {
    const round = [];
    for (let i = 0; i < matches; i++) {
      round.push({ code: generateMatchCode(matchId++) });
    }
    rounds.push(round);
    matches = Math.floor(matches / 2);
  }
  return rounds;
}

function generateMatchCode(i) {
  return "M" + i.toString().padStart(3, "0");
}
