// src/pages/PlayerRegister.js

import React, { useState } from "react";
import axios from "axios";

function PlayerRegister() {
  const [form, setForm] = useState({
    full_name: "",
    dob_ad: "",
    father_name: "",
    guardian_phone: "",
    // ...other fields
  });
  const [photo, setPhoto] = useState(null);
  const [birthCertificate, setBirthCertificate] = useState(null);
  const [status, setStatus] = useState("");

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFile = e => {
    if (e.target.name === "photo") setPhoto(e.target.files[0]);
    if (e.target.name === "birthCertificate") setBirthCertificate(e.target.files[0]);
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const data = new FormData();
    Object.entries(form).forEach(([key, value]) => data.append(key, value));
    if (photo) data.append("photo", photo);
    if (birthCertificate) data.append("birthCertificate", birthCertificate);

    try {
      await axios.post("/api/players/register", data, {
        headers: { "Content-Type": "multipart/form-data" }
      });
      setStatus("Player registered!");
    } catch (err) {
      setStatus("Registration failed: " + (err.response?.data?.message || "Error"));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="full_name" placeholder="Full Name" value={form.full_name} onChange={handleChange} />
      <input name="dob_ad" type="date" value={form.dob_ad} onChange={handleChange} />
      <input name="father_name" placeholder="Father Name" value={form.father_name} onChange={handleChange} />
      <input name="guardian_phone" placeholder="Guardian Phone" value={form.guardian_phone} onChange={handleChange} />
      {/* More fields as needed */}
      <input name="photo" type="file" onChange={handleFile} />
      <input name="birthCertificate" type="file" onChange={handleFile} />
      <button type="submit">Register</button>
      <div>{status}</div>
    </form>
  );
}

export default PlayerRegister;
