// src/pages/PlayerProfile.js

import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function PlayerProfile() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [player, setPlayer] = useState(null);
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }
    const fetchPlayer = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/players/${id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setPlayer(res.data);
      } catch {
        alert("Could not load player.");
        navigate("/dashboard");
      } finally {
        setLoading(false);
      }
    };
    fetchPlayer();
  }, [id, navigate, token]);

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center text-xl">Loading...</div>;
  }

  if (!player) {
    return null;
  }

  return (
    <div className="min-h-screen flex flex-col items-center py-10 px-2 bg-gradient-to-br from-blue-50 to-green-50">
      <div className="bg-white rounded-2xl shadow p-6 w-full max-w-xl">
        <button className="mb-4 text-blue-600" onClick={() => navigate(-1)}>&larr; Back</button>
        <div className="flex flex-col md:flex-row gap-6 items-start">
          {/* Photo */}
          {player.photo && (
            <img
              src={`/${player.photo.replace(/^uploads\//, "")}`}
              alt="Player"
              className="w-32 h-32 rounded-xl border"
              style={{ objectFit: "cover" }}
            />
          )}
          <div>
            <h2 className="text-2xl font-bold mb-2">{player.full_name_eng}</h2>
            <div className="text-lg text-gray-600 mb-2">{player.full_name_nep}</div>
            <div><b>Player ID:</b> {player.player_id}</div>
            <div><b>DOB (AD):</b> {player.dob_ad}</div>
            <div><b>DOB (BS):</b> {player.dob_bs}</div>
            <div><b>Gender:</b> {player.gender}</div>
            <div><b>Father:</b> {player.father_name}</div>
            <div><b>Mother:</b> {player.mother_name}</div>
            <div><b>Address:</b> {player.address}</div>
            <div><b>Status:</b> {player.status}</div>
            {/* Certificate Download */}
            {player.certificate && (
              <div className="mt-2">
                <a
                  href={`/${player.certificate.replace(/^uploads\//, "")}`}
                  download
                  className="text-blue-500 underline"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Download Certificate
                </a>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
