// src/components/Sidebar.js

import React from "react";
import { FaUserGraduate, FaUsers, FaTrophy, FaUserCircle, FaSignOutAlt } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

// Pass these as props: logo (src), schoolName, schoolLogo (src)
export default function Sidebar({ logo, schoolName, schoolLogo, onLogout, active="dashboard" }) {
  const navigate = useNavigate();

  // Menu config (future: add more as needed)
  const menu = [
    { key: "dashboard", icon: <FaUserGraduate />, label: "Dashboard", path: "/school-dashboard" },
    { key: "players", icon: <FaUsers />, label: "Players", path: "/school-dashboard" },
    // { key: "teams", icon: <FaUserCircle />, label: "Teams", path: "/school-dashboard/teams" },
    // { key: "tournaments", icon: <FaTrophy />, label: "Tournaments", path: "/school-dashboard/tournaments" },
    { key: "profile", icon: <FaUserCircle />, label: "Profile", path: "/school-dashboard/profile" }
  ];

  return (
    <aside className="h-screen flex flex-col bg-[#0755AA] text-white w-64 min-w-[220px] shadow-lg">
      {/* Logo at the top */}
      <div className="flex flex-col items-center py-6 border-b border-blue-900">
        {logo && (
          <img src={logo} alt="ATHLETIQ Logo" className="w-20 h-20 rounded-full mb-2 bg-white p-1 shadow" />
        )}
        <div className="flex items-center gap-2 mt-2">
          {schoolLogo && (
            <img src={schoolLogo} alt="School Logo" className="w-10 h-10 rounded shadow bg-white" />
          )}
          <span className="font-bold text-lg">{schoolName}</span>
        </div>
      </div>
      {/* Nav menu */}
      <nav className="flex-1 mt-6">
        <ul className="flex flex-col gap-1 px-2">
          {menu.map((item) => (
            <li key={item.key}>
              <button
                className={`flex items-center gap-3 w-full px-4 py-2 rounded transition ${
                  active === item.key
                    ? "bg-white/20 font-semibold"
                    : "hover:bg-white/10"
                }`}
                onClick={() => navigate(item.path)}
              >
                {item.icon}
                {item.label}
              </button>
            </li>
          ))}
        </ul>
      </nav>
      {/* Logout button always at the bottom */}
      <button
        onClick={onLogout}
        className="mb-4 mx-4 mt-auto flex items-center gap-2 px-4 py-2 bg-red-600 hover:bg-red-700 rounded text-white"
      >
        <FaSignOutAlt />
        Logout
      </button>
    </aside>
  );
}
