import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { useState } from "react";
import { sportsList } from "@/data/sportsList";
import * as Icons from "lucide-react";
import { motion } from "framer-motion";

export default function AddSportModal({ open, onClose, onAdd }) {
  const [search, setSearch] = useState("");
  const [selected, setSelected] = useState([]);

  const filtered = sportsList.filter(s =>
    s.name.toLowerCase().includes(search.toLowerCase())
  );

  // Drag, drop, and card logic would go here

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-lg rounded-2xl">
        <DialogHeader>
          <DialogTitle>Select Sports</DialogTitle>
        </DialogHeader>
        <Input
          placeholder="Search sports..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          className="mb-4"
        />
        <div className="grid grid-cols-3 gap-4">
          {filtered.map(sport => {
            const Icon = Icons[sport.icon] || Icons.Trophy;
            return (
              <motion.div
                key={sport.name}
                whileHover={{ scale: 1.08 }}
                whileTap={{ scale: 0.98 }}
                className={`rounded-lg cursor-pointer bg-athletiq-cream shadow flex flex-col items-center p-3 border border-athletiq-blue`}
                onClick={() => setSelected([...selected, { ...sport }])}
              >
                <Icon className="w-8 h-8 text-athletiq-blue mb-2" />
                <span className="font-semibold text-athletiq-navy">{sport.name}</span>
              </motion.div>
            );
          })}
        </div>
        <div className="mt-6">
          <div className="font-bold text-athletiq-navy mb-2">Selected:</div>
          <div className="flex flex-wrap gap-2">
            {selected.map((s, i) => (
              <div key={i} className="bg-athletiq-green text-white px-3 py-1 rounded-lg flex items-center gap-1">
                <span>{s.name}</span>
                <button onClick={() => setSelected(selected.filter((_, j) => j !== i))}>âœ•</button>
              </div>
            ))}
          </div>
        </div>
        <div className="flex justify-end mt-4">
          <button
            className="px-4 py-2 bg-athletiq-green text-white rounded font-semibold"
            onClick={() => { onAdd(selected); onClose(); }}
            disabled={selected.length === 0}
          >
            Add to Tournament
          </button>
        </div>
      </DialogContent>
    </Dialog>
  );
}
