// src/components/Topbar.js

import React from "react";

// Topbar receives user, school (optional), and onLogout props
export default function Topbar({ user, school, onLogout }) {
  return (
    <header className="w-full flex items-center justify-between bg-white shadow px-8 py-4 h-20 z-10">
      {/* Left: School logo + name */}
      <div className="flex items-center gap-3">
        {/* Show school logo if exists, else fallback to text logo */}
        {school && school.logo_url ? (
          <img
            src={`http://localhost:5000/uploads/${school.logo_url}`}
            alt="School Logo"
            className="h-10 w-10 rounded object-cover bg-athletiq-cream border"
          />
        ) : (
          <span className="text-xl font-extrabold text-athletiq-navy">
            ATHLETIQ
          </span>
        )}
        <span className="font-bold text-athletiq-navy text-lg">
          {school?.name || ""}
        </span>
      </div>
      {/* Right: User info + Logout */}
      <div className="flex items-center gap-5">
        <span className="font-semibold text-athletiq-navy">
          {user?.full_name || user?.email || "User"}
        </span>
        <button
          onClick={onLogout}
          className="bg-athletiq-green text-white px-4 py-2 rounded-lg font-semibold hover:bg-green-700 transition"
        >
          Logout
        </button>
      </div>
    </header>
  );
}

/*
ONBOARDING NOTES:
- Topbar.js is shared by all dashboards.
- Displays school logo (if available), school name, user info, and logout button.
- Props: user (required), school (optional), onLogout (function).
- Put this at the top of your main layout.
- Uses Tailwind CSS color palette matching ATHLETIQ.
*/
