import React from "react";
import { useLang } from "../../../contexts/LangContext";

export default function StepWelcome({ next }) {
  const { lang } = useLang();
  return (
    <div className="flex flex-col items-center justify-center min-h-[60vh]">
      <h2 className="text-3xl font-bold mb-6">
        {lang === "en" ? "Namaste & Welcome to ATHLETIQ!" : "नमस्ते, ATHLETIQ मा स्वागत छ!"}
      </h2>
      <p className="mb-8 text-lg text-gray-600 text-center max-w-md">
        {lang === "en"
          ? "Let’s get your school registered in a few simple steps. Click below to begin."
          : "केही सजिला चरणहरूमा तपाईंको विद्यालय दर्ता गरौं। सुरु गर्न तल क्लिक गर्नुहोस्।"}
      </p>
      <button
        onClick={next}
        className="py-3 px-8 bg-blue-500 text-white rounded-lg text-xl hover:bg-blue-600 transition"
      >
        {lang === "en" ? "Start Registration" : "दर्ता सुरु गर्नुहोस्"}
      </button>
    </div>
  );
}
