import React, { useState } from "react";
import axios from "axios";

/**
 * AddSportModal
 * Props:
 *  - open: show/hide
 *  - onClose: close modal
 *  - tournamentId: parent tournament
 *  - onAdded: called after successful add
 */
const SPORTS_LIST = [
  "Football",
  "Cricket",
  "Basketball",
  "Athletics",
  "Badminton",
  // ...add more or fetch from API
];

export default function AddSportModal({ open, onClose, tournamentId, onAdded }) {
  const [sport_name, setSportName] = useState("");
  const [team_size, setTeamSize] = useState("");
  const [max_teams, setMaxTeams] = useState("");
  const [rules_url, setRulesUrl] = useState("");
  const [submitting, setSubmitting] = useState(false);
  const [err, setErr] = useState("");

  if (!open) return null;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setErr("");
    try {
      await axios.post(
        `http://localhost:5000/api/tournaments/${tournamentId}/sports`,
        { sport_name, team_size: team_size || null, max_teams: max_teams || null, rules_url },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      onAdded && onAdded();
    } catch (e) {
      setErr(e.response?.data?.error || "Error adding sport.");
    }
    setSubmitting(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black/40">
      <div className="bg-white p-6 rounded-lg shadow-lg min-w-[320px] max-w-[95vw] relative">
        <button className="absolute right-3 top-3 text-xl text-gray-500 hover:text-red-500" onClick={onClose}>
          &times;
        </button>
        <h2 className="text-xl font-bold mb-4 text-athletiq-navy">Add Sport</h2>
        {err && <div className="text-red-500 mb-2">{err}</div>}
        <form onSubmit={handleSubmit} className="flex flex-col gap-4">
          <div>
            <label className="block font-semibold mb-1">Sport Name<span className="text-red-500">*</span></label>
            <select
              className="border p-2 rounded w-full"
              value={sport_name}
              onChange={e => setSportName(e.target.value)}
              required
              disabled={submitting}
            >
              <option value="">Select Sport</option>
              {SPORTS_LIST.map(s => (
                <option value={s} key={s}>{s}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block font-semibold mb-1">Team Size</label>
            <input
              className="border p-2 rounded w-full"
              type="number"
              value={team_size}
              onChange={e => setTeamSize(e.target.value)}
              placeholder="(optional)"
              disabled={submitting}
              min={1}
            />
          </div>
          <div>
            <label className="block font-semibold mb-1">Max Teams</label>
            <input
              className="border p-2 rounded w-full"
              type="number"
              value={max_teams}
              onChange={e => setMaxTeams(e.target.value)}
              placeholder="(optional)"
              disabled={submitting}
              min={1}
            />
          </div>
          <div>
            <label className="block font-semibold mb-1">Rules URL</label>
            <input
              className="border p-2 rounded w-full"
              type="url"
              value={rules_url}
              onChange={e => setRulesUrl(e.target.value)}
              placeholder="https://link-to-rules.pdf"
              disabled={submitting}
            />
          </div>
          <div className="flex justify-end gap-2 mt-4">
            <button
              type="button"
              className="px-4 py-2 rounded bg-gray-200 hover:bg-gray-300 font-semibold"
              onClick={onClose}
              disabled={submitting}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 rounded bg-athletiq-green text-white font-semibold hover:bg-green-700"
              disabled={submitting || !sport_name}
            >
              {submitting ? "Adding..." : "Add Sport"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
