// src/pages/AdminDashboard.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import DashboardLayout from "../components/DashboardLayout";
import AddSchoolModal from "../components/AddSchoolModal";
import EditSchoolModal from "../components/EditSchoolModal";
import BulkSchoolUploadModal from "../components/BulkSchoolUploadModal";
import AddPlayerModal from "../components/AddPlayerModal";
import EditPlayerModal from "../components/EditPlayerModal";
import ViewPlayerModal from "../components/ViewPlayerModal";
import BulkPlayerUploadModal from "../components/BulkPlayerUploadModal";
import ChangeAdminPasswordModal from "../components/ChangeAdminPasswordModal";
// (Future) import AddTournamentModal, EditTournamentModal, etc.

export default function AdminDashboard() {
  // --- STATE ---
  const [schools, setSchools] = useState([]);
  const [players, setPlayers] = useState([]);
  const [tournaments, setTournaments] = useState([]);
  const [view, setView] = useState("schools");
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    schools: 0,
    players: 0,
    tournaments: 0,
  });
  const [err, setErr] = useState("");
  const [changePwdSchool, setChangePwdSchool] = useState(null);

  // School modals
  const [addSchoolOpen, setAddSchoolOpen] = useState(false);
  const [bulkSchoolOpen, setBulkSchoolOpen] = useState(false);
  const [editSchool, setEditSchool] = useState(null);

  // Player modals
  const [addPlayerOpen, setAddPlayerOpen] = useState(false);
  const [editPlayer, setEditPlayer] = useState(null);
  const [viewPlayer, setViewPlayer] = useState(null);
  const [bulkPlayerOpen, setBulkPlayerOpen] = useState(false);

  // Bulk deletes & filters
  const [selectedSchoolId, setSelectedSchoolId] = useState("");
  const [selectedPlayerIds, setSelectedPlayerIds] = useState([]);

  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  // --- FETCH DATA ---
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setErr("");
      try {
        // Fetch schools & players (for schools/players/stats)
        if (["schools", "players", "stats"].includes(view)) {
          const [sRes, pRes] = await Promise.all([
            axios.get("http://localhost:5000/api/schools", {
              headers: { Authorization: `Bearer ${token}` },
            }),
            axios.get(
              `http://localhost:5000/api/players${
                user.role === "super_admin" && selectedSchoolId
                  ? `?school_id=${selectedSchoolId}`
                  : ""
              }`,
              { headers: { Authorization: `Bearer ${token}` } }
            ),
          ]);
          setSchools(sRes.data.schools || []);
          setPlayers(pRes.data.players || []);
          setStats((prev) => ({
            ...prev,
            schools: (sRes.data.schools || []).length,
            players: (pRes.data.players || []).length,
          }));
        }
        // Fetch tournaments (for tournaments/stats)
        if (["tournaments", "stats"].includes(view)) {
          const tRes = await axios.get("http://localhost:5000/api/tournaments", {
            headers: { Authorization: `Bearer ${token}` },
          });
          const list = Array.isArray(tRes.data) ? tRes.data : [];
          setTournaments(list);
          setStats((prev) => ({ ...prev, tournaments: list.length }));
        }
        setSelectedPlayerIds([]);
      } catch (e) {
        setErr(e.response?.data?.message || "Error loading data.");
      }
      setLoading(false);
    };
    fetchData();
  }, [view, token, selectedSchoolId, user.role]);

  // --- RELOAD HELPERS ---
  const reloadSchools = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/schools", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSchools(res.data.schools || []);
    } catch {
      setErr("Error reloading schools.");
    }
  };
  const reloadPlayers = async () => {
    try {
      const res = await axios.get(
        `http://localhost:5000/api/players${
          user.role === "super_admin" && selectedSchoolId
            ? `?school_id=${selectedSchoolId}`
            : ""
        }`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setPlayers(res.data.players || []);
      setSelectedPlayerIds([]);
      setStats((prev) => ({
        ...prev,
        players: res.data.players?.length || prev.players,
      }));
    } catch {
      setErr("Error reloading players.");
    }
  };

  // --- DELETE HANDLERS ---
  const handleDeleteSchool = async (id) => {
    if (!window.confirm("Delete this school?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/schools/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      reloadSchools();
    } catch {
      alert("Delete failed.");
    }
  };
  const handleDeletePlayer = async (id) => {
    if (!window.confirm("Delete this player?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/players/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      reloadPlayers();
    } catch {
      alert("Delete failed.");
    }
  };
  const handleBulkDeletePlayers = async () => {
    if (
      selectedPlayerIds.length === 0 ||
      !window.confirm("Delete selected players?")
    )
      return;
    try {
      await axios.post(
        "http://localhost:5000/api/players/bulk-delete",
        { ids: selectedPlayerIds },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      reloadPlayers();
    } catch {
      alert("Bulk delete failed.");
    }
  };

  // --- SIDEBAR LINKS ---
  const sidebarLinks = [
    {
      label: "Schools",
      icon: "🏫",
      onClick: () => setView("schools"),
      active: view === "schools",
    },
    {
      label: "Players",
      icon: "🧑‍🎓",
      onClick: () => setView("players"),
      active: view === "players",
    },
    {
      label: "Tournaments",
      icon: "🏆",
      onClick: () => setView("tournaments"),
      active: view === "tournaments",
    },
    {
      label: "Platform Stats",
      icon: "📊",
      onClick: () => setView("stats"),
      active: view === "stats",
    },
  ];

  // --- PLAYER CHECKBOX LOGIC ---
  const togglePlayerSelection = (id) => {
    setSelectedPlayerIds((prev) =>
      prev.includes(id) ? prev.filter((x) => x !== id) : [...prev, id]
    );
  };
  const handleSelectAllPlayers = (e) => {
    if (e.target.checked) {
      setSelectedPlayerIds(players.map((p) => p.id));
    } else {
      setSelectedPlayerIds([]);
    }
  };

  // --- RENDER ---
  return (
    <DashboardLayout
      user={user}
      onLogout={() => {
        localStorage.removeItem("token");
        localStorage.removeItem("user");
        window.location.href = "/login";
      }}
      sidebarLinks={sidebarLinks}
    >
      <div className="flex flex-col gap-6 w-full">
        <h1 className="text-3xl font-extrabold text-athletiq-navy mb-2">
          Super Admin Dashboard
        </h1>
        {err && <div className="text-red-500 mb-4">{err}</div>}
        {loading ? (
          <div>Loading...</div>
        ) : (
          <>
            {/* === SCHOOLS VIEW === */}
            {view === "schools" && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-bold text-athletiq-navy">
                    All Schools
                  </h2>
                  <div className="flex gap-2">
                    <button
                      onClick={() => setAddSchoolOpen(true)}
                      className="px-4 py-2 bg-athletiq-green text-white rounded hover:bg-green-700"
                    >
                      + Add School
                    </button>
                    <button
                      onClick={() => setBulkSchoolOpen(true)}
                      className="px-4 py-2 bg-athletiq-blue text-white rounded hover:bg-athletiq-navy"
                    >
                      Bulk Upload
                    </button>
                  </div>
                </div>
                <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                  <table className="min-w-full text-sm">
                    <thead>
                      <tr className="bg-athletiq-blue text-white">
                        <th className="p-2 text-left">Name</th>
                        <th className="p-2 text-left">Address</th>
                        <th className="p-2 text-left">Email</th>
                        <th className="p-2 text-left">Active</th>
                        <th className="p-2 text-left">Logo</th>
                        <th className="p-2 text-left">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {schools.map((s) => (
                        <tr key={s.id} className="border-b">
                          <td className="p-2">{s.name}</td>
                          <td className="p-2">{s.address}</td>
                          <td className="p-2">{s.email}</td>
                          <td className="p-2">
                            {s.is_active ? "Yes" : "No"}
                          </td>
                          <td className="p-2">
                            {s.logo_url ? (
                              <img
                                src={`http://localhost:5000/uploads/${s.logo_url}`}
                                alt="logo"
                                className="h-8 w-8 rounded"
                              />
                            ) : (
                              "-"
                            )}
                          </td>
                          <td className="p-2 flex gap-2">
                            <button
                              onClick={() => setEditSchool(s)}
                              className="bg-athletiq-blue text-white px-3 py-1 rounded"
                            >
                              Edit
                            </button>
                            <button
                              onClick={() => handleDeleteSchool(s.id)}
                              className="bg-red-500 text-white px-3 py-1 rounded"
                            >
                              Delete
                            </button>
                            <button
                              onClick={() => setChangePwdSchool(s)}
                              className="bg-athletiq-green text-white px-3 py-1 rounded"
                              title="Change Admin Password"
                            >
                              Change Password
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                <AddSchoolModal
                  open={addSchoolOpen}
                  onClose={() => setAddSchoolOpen(false)}
                  onAdded={reloadSchools}
                />
                <EditSchoolModal
                  open={!!editSchool}
                  school={editSchool}
                  onClose={() => setEditSchool(null)}
                  onUpdated={reloadSchools}
                />
                <BulkSchoolUploadModal
                  open={bulkSchoolOpen}
                  onClose={() => setBulkSchoolOpen(false)}
                  onUploaded={reloadSchools}
                />
                <ChangeAdminPasswordModal
                  open={!!changePwdSchool}
                  school={changePwdSchool}
                  onClose={() => setChangePwdSchool(null)}
                  onChanged={reloadSchools}
                />
              </div>
            )}

            {/* === PLAYERS VIEW === */}
            {view === "players" && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-bold text-athletiq-navy">
                    All Players
                  </h2>
                  <div className="flex gap-3 items-center">
                    {user.role === "super_admin" && (
                      <select
                        className="border p-2"
                        value={selectedSchoolId}
                        onChange={(e) => setSelectedSchoolId(e.target.value)}
                      >
                        <option value="">All Schools</option>
                        {schools.map((s) => (
                          <option key={s.id} value={s.id}>
                            {s.name}
                          </option>
                        ))}
                      </select>
                    )}
                    <button
                      onClick={() => setAddPlayerOpen(true)}
                      className="px-4 py-2 bg-athletiq-green text-white rounded"
                    >
                      + Add Player
                    </button>
                    <button
                      onClick={() => setBulkPlayerOpen(true)}
                      disabled={
                        user.role === "super_admin" && !selectedSchoolId
                      }
                      className="px-4 py-2 bg-athletiq-blue text-white rounded"
                    >
                      Bulk Upload
                    </button>
                    <button
                      onClick={handleBulkDeletePlayers}
                      disabled={selectedPlayerIds.length === 0}
                      className="px-4 py-2 bg-red-500 text-white rounded"
                    >
                      Delete Selected
                    </button>
                  </div>
                </div>
                <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                  <table className="min-w-full text-sm">
                    <thead>
                      <tr className="bg-athletiq-blue text-white">
                        <th className="p-2">
                          <input
                            type="checkbox"
                            checked={
                              selectedPlayerIds.length > 0 &&
                              selectedPlayerIds.length === players.length
                            }
                            onChange={handleSelectAllPlayers}
                          />
                        </th>
                        <th className="p-2 text-left">Name</th>
                        <th className="p-2 text-left">School</th>
                        <th className="p-2 text-left">DOB</th>
                        <th className="p-2 text-left">Phone</th>
                        <th className="p-2 text-left">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {players.map((p) => (
                        <tr key={p.id} className="border-b">
                          <td className="p-2">
                            <input
                              type="checkbox"
                              checked={selectedPlayerIds.includes(p.id)}
                              onChange={() => togglePlayerSelection(p.id)}
                            />
                          </td>
                          <td className="p-2">{p.full_name}</td>
                          <td className="p-2">{p.school_name || p.school_id}</td>
                          <td className="p-2">{p.dob}</td>
                          <td className="p-2">
                            {p.guardian_phone || p.contact_no}
                          </td>
                          <td className="p-2 flex gap-2">
                            <button
                              onClick={() => setViewPlayer(p)}
                              className="bg-athletiq-blue text-white px-3 py-1 rounded"
                            >
                              View
                            </button>
                            <button
                              onClick={() => setEditPlayer(p)}
                              className="bg-athletiq-green text-white px-3 py-1 rounded"
                            >
                              Edit
                            </button>
                            <button
                              onClick={() => handleDeletePlayer(p.id)}
                              className="bg-red-500 text-white px-3 py-1 rounded"
                            >
                              Delete
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
                <AddPlayerModal
                  open={addPlayerOpen}
                  onClose={() => setAddPlayerOpen(false)}
                  onAdded={reloadPlayers}
                  schools={schools}
                  defaultSchoolId={
                    user.role === "school_admin" ? user.school_id : undefined
                  }
                />
                <EditPlayerModal
                  open={!!editPlayer}
                  player={editPlayer}
                  onClose={() => setEditPlayer(null)}
                  schools={schools}
                  onUpdated={reloadPlayers}
                />
                <ViewPlayerModal
                  open={!!viewPlayer}
                  player={viewPlayer}
                  onClose={() => setViewPlayer(null)}
                />
                <BulkPlayerUploadModal
                  open={bulkPlayerOpen}
                  onClose={() => setBulkPlayerOpen(false)}
                  onUploaded={reloadPlayers}
                  schoolId={user.role === "super_admin" ? selectedSchoolId : undefined}
                />
              </div>
            )}

            {/* === TOURNAMENTS VIEW === */}
            {view === "tournaments" && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-bold text-athletiq-navy">
                    All Tournaments
                  </h2>
                  {/* Future: <button>+ Add Tournament</button> */}
                </div>
                {tournaments.length === 0 ? (
                  <div>No tournaments found.</div>
                ) : (
                  <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                    <table className="min-w-full text-sm">
                      <thead>
                        <tr className="bg-athletiq-blue text-white">
                          <th className="p-2 text-left">Name</th>
                          <th className="p-2 text-left">Code</th>
                          <th className="p-2 text-left">Sports</th>
                          <th className="p-2 text-left">Start Date</th>
                          <th className="p-2 text-left">Status</th>
                          <th className="p-2 text-left">Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {tournaments.map((t) => (
                          <tr key={t.id} className="border-b">
                            <td className="p-2">{t.name}</td>
                            <td className="p-2">{t.code}</td>
                            <td className="p-2">
                              {(Array.isArray(t.sports)
                                ? t.sports.join(", ")
                                : JSON.parse(t.sports || "[]").join(", ")) ||
                                "-"}
                            </td>
                            <td className="p-2">
                              {t.start_date?.slice(0, 10)}
                            </td>
                            <td className="p-2">{t.status || "Active"}</td>
                            <td className="p-2 flex gap-2">
                              {/* Future: Edit/Delete buttons */}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            )}

            {/* === STATS VIEW === */}
            {view === "stats" && (
              <div>
                <h2 className="text-xl font-bold text-athletiq-navy mb-4">
                  Platform Stats
                </h2>
                <div className="flex gap-8">
                  <div className="bg-athletiq-blue px-6 py-4 rounded text-center shadow text-white">
                    <div className="text-3xl font-bold">{stats.schools}</div>
                    <div>Schools</div>
                  </div>
                  <div className="bg-athletiq-green px-6 py-4 rounded text-center shadow text-white">
                    <div className="text-3xl font-bold">{stats.players}</div>
                    <div>Players</div>
                  </div>
                  <div className="bg-athletiq-blue px-6 py-4 rounded text-center shadow text-white">
                    <div className="text-3xl font-bold">{stats.tournaments}</div>
                    <div>Tournaments</div>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </DashboardLayout>
  );
}
