// src/pages/SchoolDashboard.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import DashboardLayout from "../components/DashboardLayout";
import EditSchoolModal from "../components/EditSchoolModal";
import AddPlayerModal from "../components/AddPlayerModal";
import EditPlayerModal from "../components/EditPlayerModal";
import ViewPlayerModal from "../components/ViewPlayerModal";

export default function SchoolDashboard() {
  // 1. Get user & token from localStorage (school admin must be logged in)
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  const token = localStorage.getItem("token");

  // 2. State for school, players, loading, error, modals
  const [school, setSchool] = useState(null);
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [editSchoolOpen, setEditSchoolOpen] = useState(false);
  const [addPlayerOpen, setAddPlayerOpen] = useState(false);
  const [editPlayer, setEditPlayer] = useState(null);
  const [viewPlayer, setViewPlayer] = useState(null);

  // 3. Fetch school info and all players for this school admin
  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      setErr("");
      try {
        let schoolData = school;
        // Only fetch school if not loaded yet (and school_admin)
        if (!school && user.role === "school_admin") {
          const schoolRes = await axios.get("http://localhost:5000/api/schools/me", {
            headers: { Authorization: `Bearer ${token}` }
          });
          schoolData = schoolRes.data.school;
          setSchool(schoolData);
        }
        // Get all players for this admin (filtered in backend by JWT)
        const playersRes = await axios.get(
          `http://localhost:5000/api/players`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setPlayers(playersRes.data.players || []);
      } catch (error) {
        setErr(error?.response?.data?.message || "Failed to load school or players.");
      }
      setLoading(false);
    }
    if (user.role === "school_admin" && token) fetchData();
    // eslint-disable-next-line
  }, [token]);

  // 4. Handlers
  const handleSchoolUpdated = (updatedSchool) => {
    setSchool(updatedSchool || school);
    setEditSchoolOpen(false);
  };

  const handlePlayerAdded = () => {
    axios
      .get(`http://localhost:5000/api/players`, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then((res) => setPlayers(res.data.players || []))
      .catch(() => {});
    setAddPlayerOpen(false);
  };

  const handlePlayerUpdated = () => {
    axios
      .get(`http://localhost:5000/api/players`, {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then((res) => setPlayers(res.data.players || []))
      .catch(() => {});
    setEditPlayer(null);
  };

  const handleDeletePlayer = async (playerId) => {
    if (!window.confirm("Delete this player?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/players/${playerId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPlayers((prev) => prev.filter((p) => p.id !== playerId));
    } catch (error) {
      alert("Delete failed.");
    }
  };

  // 5. Logout handler
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/login";
  };

  // 6. UI
  if (user.role !== "school_admin") {
    return (
      <div className="p-8">
        <h1 className="text-2xl font-bold mb-2">Unauthorized</h1>
        <p>You must be logged in as a school admin to access this page.</p>
      </div>
    );
  }

  return (
    <DashboardLayout school={school} user={user} onLogout={handleLogout}>
      <div className="flex flex-col gap-3">
        <div className="flex justify-between items-center mb-2">
          <div>
            <h1 className="text-3xl font-extrabold text-athletiq-navy mb-1">Welcome, {user.full_name || user.email}</h1>
            <div className="text-lg">
              <span className="font-semibold text-athletiq-blue">School:</span>{" "}
              <span className="font-bold text-athletiq-navy">{school?.name}</span>
              <span className="text-athletiq-gray ml-3">| School Code: <span className="font-semibold">{school?.school_code}</span></span>
            </div>
          </div>
          <button
            className="px-6 py-2 bg-athletiq-navy text-white rounded-lg font-semibold hover:bg-athletiq-blue transition"
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>

        {/* Onboarding progress bar */}
        <div className="bg-athletiq-green/20 rounded-lg p-3 flex items-center mb-4">
          <span className="text-athletiq-navy text-lg font-medium flex items-center">
            <svg width="24" height="24" fill="currentColor" className="text-athletiq-green mr-2"><circle cx="12" cy="12" r="10"/><path d="M7 13l3 3 7-7" stroke="#fff" strokeWidth="2" fill="none"/></svg>
            Your school onboarding is 80% complete!
          </span>
        </div>

        {/* Stat Cards */}
        <div className="flex flex-wrap gap-4 mb-4">
          <div className="flex-1 min-w-[200px] bg-athletiq-blue text-white rounded-lg p-4 flex items-center gap-3 shadow-md">
            <span className="text-2xl"><svg fill="none" stroke="currentColor" strokeWidth="2" width="28" height="28"><path d="M15 19l-7-7 7-7"/></svg></span>
            <div>
              <div className="font-bold text-lg">Registered Players</div>
              <div className="text-2xl">{players.length}</div>
            </div>
          </div>
          <div className="flex-1 min-w-[200px] bg-athletiq-blue text-white rounded-lg p-4 flex items-center gap-3 shadow-md">
            <span className="text-2xl"><svg fill="none" stroke="currentColor" strokeWidth="2" width="28" height="28"><path d="M12 4v16m8-8H4"/></svg></span>
            <div>
              <div className="font-bold text-lg">Tournaments Participated</div>
              <div className="text-2xl">2</div>
            </div>
          </div>
          <div className="flex-1 min-w-[200px] bg-athletiq-blue text-white rounded-lg p-4 flex items-center gap-3 shadow-md">
            <span className="text-2xl"><svg fill="none" stroke="currentColor" strokeWidth="2" width="28" height="28"><circle cx="12" cy="12" r="10"/><path d="M9 9l6 6M15 9l-6 6"/></svg></span>
            <div>
              <div className="font-bold text-lg">Pending Verifications</div>
              <div className="text-2xl">5</div>
            </div>
          </div>
          <div className="flex-1 min-w-[200px] bg-athletiq-blue text-white rounded-lg p-4 flex items-center gap-3 shadow-md">
            <span className="text-2xl"><svg fill="none" stroke="currentColor" strokeWidth="2" width="28" height="28"><rect width="20" height="14" x="2" y="5" rx="2"/><path d="M16 3v2M8 3v2"/></svg></span>
            <div>
              <div className="font-bold text-lg">Missing Docs</div>
              <div className="text-2xl">3</div>
            </div>
          </div>
        </div>

        {/* Player Table + Add/Bulk Buttons */}
        <div className="flex justify-between items-center mb-2">
          <h2 className="text-xl font-semibold text-athletiq-navy">Player Table</h2>
          <div className="flex gap-3">
            <button
              className="px-5 py-2 bg-white text-athletiq-navy border border-athletiq-navy rounded-lg font-semibold hover:bg-athletiq-cream transition"
              onClick={() => setAddPlayerOpen(true)}
            >
              Add Player
            </button>
            <button
              className="px-5 py-2 bg-athletiq-green text-white rounded-lg font-semibold hover:bg-green-700 transition"
            >
              Bulk Upload
            </button>
          </div>
        </div>
        <div className="bg-white rounded-lg shadow-md overflow-x-auto">
          <table className="min-w-full text-sm">
            <thead>
              <tr className="bg-athletiq-blue text-white">
                <th className="p-2 text-left">Player Name</th>
                <th className="p-2 text-left">DOB</th>
                <th className="p-2 text-left">Guardian Phone</th>
                <th className="p-2 text-left">Profile</th>
                <th className="p-2 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {players.length === 0 && (
                <tr>
                  <td className="p-2" colSpan={5}>
                    No players found.
                  </td>
                </tr>
              )}
              {players.map((p) => (
                <tr key={p.id} className="border-b last:border-none">
                  <td className="p-2">{p.full_name}</td>
                  <td className="p-2">{p.dob}</td>
                  <td className="p-2">{p.guardian_phone || "-"}</td>
                  <td className="p-2 flex items-center gap-2">
                    {/* Placeholder profile icons */}
                    <span className="inline-block bg-athletiq-gray text-white rounded-full h-6 w-6 flex items-center justify-center">
                      <svg width="16" height="16" fill="none" stroke="currentColor" strokeWidth="2"><circle cx="8" cy="8" r="7"/><circle cx="8" cy="6" r="2"/><path d="M4 12a4 4 0 018 0"/></svg>
                    </span>
                    <span className="inline-block bg-athletiq-green text-white rounded-full h-6 w-6 flex items-center justify-center">
                      <svg width="16" height="16" fill="none" stroke="currentColor" strokeWidth="2"><rect width="12" height="12" x="2" y="2" rx="2"/></svg>
                    </span>
                  </td>
                  <td className="p-2 flex gap-2">
                    <button
                      className="bg-athletiq-blue text-white px-2 py-1 rounded hover:bg-athletiq-navy"
                      onClick={() => setEditPlayer(p)}
                    >
                      Edit
                    </button>
                    <button
                      className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-700"
                      onClick={() => handleDeletePlayer(p.id)}
                    >
                      Delete
                    </button>
                    <button
                      className="bg-gray-200 text-athletiq-navy px-2 py-1 rounded hover:bg-gray-300"
                      onClick={() => setViewPlayer(p)}
                    >
                      View
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Upload Notice */}
        <div className="bg-athletiq-green/10 rounded-lg p-3 mt-4 flex items-center text-athletiq-navy">
          <span className="mr-2 text-athletiq-blue font-bold">i</span>
          5 players need profile photos. Click here to upload.
        </div>
      </div>

      {/* Modals */}
      {editSchoolOpen && school && (
        <EditSchoolModal
          open={editSchoolOpen}
          school={school}
          onClose={() => setEditSchoolOpen(false)}
          onUpdated={handleSchoolUpdated}
        />
      )}
      {addPlayerOpen && (
        <AddPlayerModal
          open={addPlayerOpen}
          onClose={() => setAddPlayerOpen(false)}
          onAdded={handlePlayerAdded}
          schools={[school]}
        />
      )}
      {editPlayer && (
        <EditPlayerModal
          open={!!editPlayer}
          onClose={() => setEditPlayer(null)}
          player={editPlayer}
          schools={[school]}
          onUpdated={handlePlayerUpdated}
        />
      )}
      {viewPlayer && (
        <ViewPlayerModal
          open={!!viewPlayer}
          onClose={() => setViewPlayer(null)}
          player={viewPlayer}
          schools={[school]}
        />
      )}
    </DashboardLayout>
  );
}
