// src/components/Sidebar.js

import React from "react";
import { Link, useLocation } from "react-router-dom";

// Sidebar menu items for each user role
const menuItemsByRole = {
  super_admin: [
    { label: "Dashboard", path: "/admin", icon: "üè†" },
    { label: "Schools", path: "/admin?tab=schools", icon: "üè´" },
    { label: "Players", path: "/admin?tab=players", icon: "üë§" },
    { label: "Stats", path: "/admin?tab=stats", icon: "üìä" }
  ],
  school_admin: [
    { label: "Dashboard", path: "/school-dashboard", icon: "üè†" },
    { label: "Players", path: "/school-dashboard?tab=players", icon: "üë§" },
    { label: "Bulk Upload", path: "/school-dashboard?tab=bulk", icon: "‚¨ÜÔ∏è" },
    { label: "School Info", path: "/school-dashboard?tab=school", icon: "üè´" }
  ],
  // Extend for more roles as you add them!
};

export default function Sidebar({ role }) {
  const location = useLocation();
  const menu = menuItemsByRole[role] || [];

  return (
    <aside className="bg-athletiq-navy text-white w-60 min-h-screen flex flex-col py-8 shadow-lg">
      {/* Logo at the top */}
      <div className="flex justify-center mb-10">
        <img
          src="/logo.png"
          alt="ATHLETIQ"
          className="h-12"
        />
      </div>
      {/* Navigation menu */}
      <nav className="flex flex-col gap-2 px-6">
        {menu.map((item) => {
          // Highlight active link
          const isActive = location.pathname + location.search === item.path;
          return (
            <Link
              to={item.path}
              key={item.label}
              className={`flex items-center gap-3 py-2 px-4 rounded-lg font-semibold transition ${
                isActive
                  ? "bg-athletiq-blue text-white shadow"
                  : "hover:bg-athletiq-blue/70"
              }`}
            >
              <span className="text-xl">{item.icon}</span>
              <span>{item.label}</span>
            </Link>
          );
        })}
      </nav>
    </aside>
  );
}

/*
ONBOARDING NOTES:
- Sidebar.js is a universal sidebar for all dashboards.
- Menu items change by user role (super_admin, school_admin, etc).
- Add more roles to menuItemsByRole as needed.
- Logo file should be placed at /public/logo.png.
- Uses Tailwind CSS and role-aware color theme.
- Uses React Router's Link for navigation.
- Can be extended to show version, app info, or help at the bottom.
*/
