/**
 * ATHLETIQ – Player Registration Page (Frontend)
 * -----------------------------------------------
 * This React component lets users register a new player profile.
 * - All files and data are sent to the backend `/api/players/register` endpoint.
 * - Supports photo and birth certificate uploads.
 * - All date handling is AD only.
 * - All Node/Express/server imports are removed—this is pure frontend code!
 * 
 * Onboarding for new developers:
 * 1. All field names must match backend expectations.
 * 2. Backend handles resizing, storage, and validation.
 * 3. To localize UI, wrap visible text with your i18n solution as needed.
 * 4. To prefill guardian info, use the logged-in user's state (not included here).
 */

import React, { useState } from "react";
import axios from "axios";

const initialState = {
  full_name: "",
  full_name_eng: "",
  full_name_nep: "",
  dob_ad: "",
  gender: "",
  father_name: "",
  mother_name: "",
  guardian_phone: "",
  guardian_email: "",
  address: "",
  // Add other backend-expected fields as needed
};

const PlayerRegister = () => {
  const [form, setForm] = useState(initialState);
  const [photo, setPhoto] = useState(null);
  const [birthCertificate, setBirthCertificate] = useState(null);
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  // Handles all text/input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handles file input
  const handleFile = (e) => {
    if (e.target.name === "photo") setPhoto(e.target.files[0]);
    if (e.target.name === "birthCertificate") setBirthCertificate(e.target.files[0]);
  };

  // Handles form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("");
    setLoading(true);
    const data = new FormData();
    Object.entries(form).forEach(([key, value]) => data.append(key, value));
    if (photo) data.append("photo", photo);
    if (birthCertificate) data.append("birthCertificate", birthCertificate);

    try {
      const res = await axios.post("/api/players/register", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setStatus("✅ Player registered successfully!");
      setForm(initialState);
      setPhoto(null);
      setBirthCertificate(null);
    } catch (err) {
      setStatus(
        "❌ " +
          (err.response?.data?.message ||
            err.message ||
            "Registration failed. Try again.")
      );
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 480, margin: "2rem auto", padding: "2rem", border: "1px solid #ddd", borderRadius: 12, background: "#fff" }}>
      <h2>Player Registration</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label>
          Full Name (English or Nepali)<br />
          <input
            type="text"
            name="full_name"
            value={form.full_name}
            onChange={handleChange}
            required
            placeholder="Full Name"
          />
        </label>
        <br />
        <label>
          Full Name (English)
          <input
            type="text"
            name="full_name_eng"
            value={form.full_name_eng}
            onChange={handleChange}
            placeholder="(If available)"
          />
        </label>
        <br />
        <label>
          Full Name (Nepali)
          <input
            type="text"
            name="full_name_nep"
            value={form.full_name_nep}
            onChange={handleChange}
            placeholder="(यदि उपलब्ध छ भने)"
          />
        </label>
        <br />
        <label>
          Date of Birth (AD)
          <input
            type="date"
            name="dob_ad"
            value={form.dob_ad}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Gender
          <select name="gender" value={form.gender} onChange={handleChange} required>
            <option value="">--Select--</option>
            <option value="M">Male/छोरा</option>
            <option value="F">Female/छोरी</option>
            <option value="O">Other/अन्य</option>
          </select>
        </label>
        <br />
        <label>
          Father Name
          <input
            type="text"
            name="father_name"
            value={form.father_name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Mother Name
          <input
            type="text"
            name="mother_name"
            value={form.mother_name}
            onChange={handleChange}
            placeholder="(If available)"
          />
        </label>
        <br />
        <label>
          Guardian Phone
          <input
            type="tel"
            name="guardian_phone"
            value={form.guardian_phone}
            onChange={handleChange}
            required
            pattern="^[0-9]{10}$"
            placeholder="98XXXXXXXX"
          />
        </label>
        <br />
        <label>
          Guardian Email
          <input
            type="email"
            name="guardian_email"
            value={form.guardian_email}
            onChange={handleChange}
            placeholder="(If available)"
          />
        </label>
        <br />
        <label>
          Address
          <input
            type="text"
            name="address"
            value={form.address}
            onChange={handleChange}
            placeholder="Permanent Address"
          />
        </label>
        <br />
        <label>
          Photo (passport size)
          <input
            type="file"
            name="photo"
            accept="image/*"
            onChange={handleFile}
            required
          />
        </label>
        <br />
        <label>
          Birth Certificate
          <input
            type="file"
            name="birthCertificate"
            accept="image/*,application/pdf"
            onChange={handleFile}
            required
          />
        </label>
        <br /><br />
        <button type="submit" disabled={loading}>
          {loading ? "Registering..." : "Register Player"}
        </button>
      </form>
      {status && (
        <div style={{ marginTop: "1rem", fontWeight: "bold", color: status.startsWith("✅") ? "green" : "red" }}>
          {status}
        </div>
      )}
    </div>
  );
};

export default PlayerRegister;
