import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [schoolName, setSchoolName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [err, setErr] = useState("");
  const [success, setSuccess] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setErr(""); setSuccess(""); setLoading(true);
    try {
      const url =
        process.env.REACT_APP_API_URL ||
        "http://localhost:5000/api/school/register";
      const data = { school_name: schoolName, email, password };
      await axios.post(url, data);
      setSuccess("School admin registered! You can login now.");
      setTimeout(() => navigate("/login"), 1300);
    } catch (error) {
      setErr(error?.response?.data?.message || "Registration failed");
    }
    setLoading(false);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-blue-50">
      <form
        className="bg-white p-8 rounded shadow w-full max-w-md"
        onSubmit={handleRegister}
      >
        <h2 className="text-xl font-bold mb-4">Register School Admin</h2>
        <input
          className="w-full border p-2 mb-2"
          placeholder="School Name"
          value={schoolName}
          onChange={e => setSchoolName(e.target.value)}
          required
        />
        <input
          className="w-full border p-2 mb-2"
          placeholder="Admin Email"
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <input
          className="w-full border p-2 mb-2"
          placeholder="Password"
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        {err && <div className="text-red-500 mb-2">{err}</div>}
        {success && <div className="text-green-500 mb-2">{success}</div>}
        <button
          className="w-full py-2 bg-green-600 text-white rounded"
          type="submit"
          disabled={loading}
        >
          {loading ? "Registering..." : "Register"}
        </button>
        <div className="mt-4 text-sm text-center">
          Already have an account?{" "}
          <span
            className="text-blue-600 underline cursor-pointer"
            onClick={() => navigate("/login")}
          >
            Login here
          </span>
        </div>
      </form>
    </div>
  );
}
