import React from "react";
import { useTranslation } from "react-i18next";

export default function StepWelcome({ next }) {
  const { t, i18n } = useTranslation();
  return (
    <div className="flex flex-col items-center justify-center min-h-[60vh]">
      <h2 className="text-3xl font-bold mb-6">{t("welcome")}</h2>
      <p className="mb-8 text-lg text-gray-600 text-center max-w-md">{t("start_registration")}</p>
      <button
        onClick={next}
        className="py-3 px-8 bg-blue-500 text-white rounded-lg text-xl hover:bg-blue-600 transition"
      >
        {t("start_registration")}
      </button>
      <div className="mt-6 flex gap-2">
        <button onClick={() => i18n.changeLanguage("en")} className="px-2 py-1 bg-blue-500 text-white rounded">EN</button>
        <button onClick={() => i18n.changeLanguage("np")} className="px-2 py-1 bg-green-600 text-white rounded">рдиреЗ</button>
      </div>
    </div>
  );
}
