import React, { useEffect, useState } from "react";
import API from "../api/axios";
import { useNavigate } from "react-router-dom";

const AdminDashboard = () => {
  const [stats, setStats] = useState(null);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    API.get("/admin/dashboard")
      .then((res) => setStats(res.data))
      .catch((err) => setError("Failed to load stats: " + (err.response?.data?.message || err.message)));
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  if (error) return <div>{error}</div>;
  if (!stats) return <div>Loading...</div>;

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-6">Super Admin Dashboard</h1>
      <button className="bg-red-500 text-white px-3 py-1 rounded mb-6" onClick={handleLogout}>Logout</button>
      <ul className="space-y-2">
        <li>Total Schools: {stats.total_schools}</li>
        <li>Total Players: {stats.total_players}</li>
        <li>Total Teams: {stats.total_teams}</li>
        <li>Total Tournaments: {stats.total_tournaments}</li>
      </ul>
    </div>
  );
};

export default AdminDashboard;
