// src/pages/TournamentSetup.jsx

import React, { useState } from "react";
import StepSports from "../components/Tournament/StepSports";
import StepSportDetails from "../components/Tournament/StepSportDetails";
import StepMatchFormat from "../components/Tournament/StepMatchFormat";
import ConfirmTournament from "../components/Tournament/ConfirmTournament";

const steps = [
  "Select Sports",
  "Sport Details",
  "Match Format",
  "Confirm & Launch"
];

export default function TournamentSetup() {
  const [currentStep, setCurrentStep] = useState(0);
  const [tournamentData, setTournamentData] = useState({
    sports: [],
    sportDetails: {},
    matchFormat: {},
  });

  const goNext = () => {
    if (currentStep < steps.length - 1) setCurrentStep(currentStep + 1);
  };

  const goBack = () => {
    if (currentStep > 0) setCurrentStep(currentStep - 1);
  };

  const updateData = (key, value) => {
    setTournamentData(prev => ({ ...prev, [key]: value }));
  };

  const renderStep = () => {
    switch (currentStep) {
      case 0:
        return (
          <StepSports
            data={tournamentData.sports}
            onNext={(data) => {
              updateData("sports", data);
              goNext();
            }}
          />
        );
      case 1:
        return (
          <StepSportDetails
            sports={tournamentData.sports}
            data={tournamentData.sportDetails}
            onNext={(data) => {
              updateData("sportDetails", data);
              goNext();
            }}
            onBack={goBack}
          />
        );
      case 2:
        return (
          <StepMatchFormat
            data={tournamentData.matchFormat}
            onNext={(data) => {
              updateData("matchFormat", data);
              goNext();
            }}
            onBack={goBack}
          />
        );
      case 3:
        return (
          <ConfirmTournament
            tournamentData={tournamentData}
            onBack={goBack}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h2 className="text-xl font-bold mb-4">Tournament Setup</h2>
      <div className="mb-6">
        {steps.map((label, index) => (
          <span
            key={index}
            className={`inline-block px-4 py-2 rounded-full mr-2 ${
              index === currentStep
                ? "bg-athletiq-blue text-white"
                : "bg-gray-200 text-gray-700"
            }`}
          >
            {label}
          </span>
        ))}
      </div>
      {renderStep()}
    </div>
  );
}
