// src/pages/AdminDashboard.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import AddSchoolModal from "../components/AddSchoolModal";
import EditSchoolModal from "../components/EditSchoolModal";
import AddPlayerModal from "../components/AddPlayerModal";
import EditPlayerModal from "../components/EditPlayerModal";

export default function AdminDashboard() {
  const [schools, setSchools] = useState([]);
  const [players, setPlayers] = useState([]);
  const [view, setView] = useState("schools");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const [addSchoolOpen, setAddSchoolOpen] = useState(false);
  const [editSchool, setEditSchool] = useState(null);

  const [addPlayerOpen, setAddPlayerOpen] = useState(false);
  const [editPlayer, setEditPlayer] = useState(null);

  const token = localStorage.getItem("token");

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      setError("");
      try {
        const [schoolsRes, playersRes] = await Promise.all([
          axios.get("http://localhost:5000/api/schools", { headers: { Authorization: `Bearer ${token}` } }),
          axios.get("http://localhost:5000/api/players", { headers: { Authorization: `Bearer ${token}` } }),
        ]);
        setSchools(schoolsRes.data.schools);
        setPlayers(playersRes.data.players);
      } catch (err) {
        setError(err.response?.data?.message || "Error loading data");
      }
      setLoading(false);
    }
    fetchData();
  }, [token]);

  const reloadSchools = () => {
    axios.get("http://localhost:5000/api/schools", { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setSchools(res.data.schools))
      .catch(() => {});
  };

  const reloadPlayers = () => {
    axios.get("http://localhost:5000/api/players", { headers: { Authorization: `Bearer ${token}` } })
      .then(res => setPlayers(res.data.players))
      .catch(() => {});
  };

  const handleDeleteSchool = async (id) => {
    if (!window.confirm("Delete this school?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/schools/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      reloadSchools();
    } catch (err) {
      alert(err.response?.data?.message || "Delete failed");
    }
  };

  const handleDeletePlayer = async (id) => {
    if (!window.confirm("Delete this player?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/players/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      reloadPlayers();
    } catch (err) {
      alert(err.response?.data?.message || "Delete failed");
    }
  };

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-3xl mb-4 font-bold">Admin Dashboard</h1>

      <div className="mb-4">
        <button onClick={() => setView("schools")} className={`mr-4 ${view === "schools" ? "font-bold" : ""}`}>Schools</button>
        <button onClick={() => setView("players")} className={`${view === "players" ? "font-bold" : ""}`}>Players</button>
      </div>

      {error && <div className="text-red-600 mb-4">{error}</div>}

      {loading ? <div>Loading...</div> : null}

      {view === "schools" && !loading && (
        <>
          <button className="mb-2 px-4 py-2 bg-green-600 text-white rounded" onClick={() => setAddSchoolOpen(true)}>Add School</button>
          <table className="min-w-full border border-gray-300 rounded overflow-hidden">
            <thead>
              <tr className="bg-blue-600 text-white">
                <th className="p-2">Name</th>
                <th className="p-2">Address</th>
                <th className="p-2">Email</th>
                <th className="p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {schools.map(school => (
                <tr key={school.id} className="border-t">
                  <td className="p-2">{school.name}</td>
                  <td className="p-2">{school.address}</td>
                  <td className="p-2">{school.email}</td>
                  <td className="p-2 space-x-2">
                    <button onClick={() => setEditSchool(school)} className="bg-blue-600 text-white px-2 rounded">Edit</button>
                    <button onClick={() => handleDeleteSchool(school.id)} className="bg-red-600 text-white px-2 rounded">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <AddSchoolModal open={addSchoolOpen} onClose={() => setAddSchoolOpen(false)} onAdded={reloadSchools} />
          <EditSchoolModal school={editSchool} open={!!editSchool} onClose={() => setEditSchool(null)} onUpdated={reloadSchools} />
        </>
      )}

      {view === "players" && !loading && (
        <>
          <button className="mb-2 px-4 py-2 bg-green-600 text-white rounded" onClick={() => setAddPlayerOpen(true)}>Add Player</button>
          <table className="min-w-full border border-gray-300 rounded overflow-hidden">
            <thead>
              <tr className="bg-blue-600 text-white">
                <th className="p-2">Name</th>
                <th className="p-2">School</th>
                <th className="p-2">DOB</th>
                <th className="p-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {players.map(player => (
                <tr key={player.id} className="border-t">
                  <td className="p-2">{player.full_name}</td>
                  <td className="p-2">{player.school_name || player.school_id}</td>
                  <td className="p-2">{player.dob}</td>
                  <td className="p-2 space-x-2">
                    <button onClick={() => setEditPlayer(player)} className="bg-blue-600 text-white px-2 rounded">Edit</button>
                    <button onClick={() => handleDeletePlayer(player.id)} className="bg-red-600 text-white px-2 rounded">Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <AddPlayerModal open={addPlayerOpen} onClose={() => setAddPlayerOpen(false)} onAdded={reloadPlayers} />
          <EditPlayerModal player={editPlayer} open={!!editPlayer} onClose={() => setEditPlayer(null)} onUpdated={reloadPlayers} />
        </>
      )}
    </div>
  );
}
