import React, { useState, useEffect, useRef } from "react";
import axios from "axios";

export default function AddPlayerModal({ open, onClose, onAdded, schools }) {
  const [form, setForm] = useState({
    full_name: "",
    dob: "",
    school_id: schools[0]?.id || "",
    gender: "",
    main_sport: "",
    guardian_phone: "",
  });
  const [photoFile, setPhotoFile] = useState(null);
  const [certFile, setCertFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");
  const fileInputPhoto = useRef();
  const fileInputCert = useRef();

  // Reset form when opening
  useEffect(() => {
    if (open) {
      setForm({
        full_name: "",
        dob: "",
        school_id: schools[0]?.id || "",
        gender: "",
        main_sport: "",
        guardian_phone: "",
      });
      setPhotoFile(null);
      setCertFile(null);
      setErr("");
    }
  }, [open, schools]);

  if (!open) return null;

  const handleChange = e => {
    let { name, value } = e.target;
    // Convert school_id to number
    if (name === "school_id") value = Number(value);
    setForm(f => ({ ...f, [name]: value }));
  };

  const handlePhoto = e => setPhotoFile(e.target.files[0]);
  const handleCert = e => setCertFile(e.target.files[0]);

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    setErr("");
    try {
      const data = new FormData();

      // Always make sure school_id is a number before submitting
      let fixedForm = { ...form, school_id: Number(form.school_id) };

      Object.entries(fixedForm).forEach(([k, v]) => {
        if (v !== undefined && v !== null && v !== "") data.append(k, v);
      });
      if (photoFile) data.append("photo", photoFile);
      if (certFile) data.append("birth_cert", certFile);

      const token = localStorage.getItem("token");
      const res = await axios.post(
        "http://localhost:5000/api/players/register",
        data,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      onAdded(res.data); // Pass up the response, let parent reload
      onClose();
    } catch (error) {
      setErr(error?.response?.data?.message || "Add player failed.");
    }
    setLoading(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-30">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-lg relative">
        <button className="absolute top-2 right-3 text-lg" onClick={onClose}>
          &times;
        </button>
        <h2 className="text-xl font-bold mb-4">Add Player</h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <div>
            <label className="block text-sm mb-1">Full Name *</label>
            <input
              className="w-full border p-2 rounded"
              name="full_name"
              value={form.full_name}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Date of Birth *</label>
            <input
              className="w-full border p-2 rounded"
              name="dob"
              type="date"
              value={form.dob}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-sm mb-1">School *</label>
            <select
              className="w-full border p-2 rounded"
              name="school_id"
              value={form.school_id}
              onChange={handleChange}
              required
            >
              {schools.map(s => (
                <option value={s.id} key={s.id}>
                  {s.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm mb-1">Gender *</label>
            <select
              className="w-full border p-2 rounded"
              name="gender"
              value={form.gender}
              onChange={handleChange}
              required
            >
              <option value="">Select...</option>
              <option value="M">Male</option>
              <option value="F">Female</option>
              <option value="O">Other</option>
            </select>
          </div>
          <div>
            <label className="block text-sm mb-1">Main Sport *</label>
            <input
              className="w-full border p-2 rounded"
              name="main_sport"
              value={form.main_sport}
              onChange={handleChange}
              required
              placeholder="e.g. Football"
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Guardian Phone</label>
            <input
              className="w-full border p-2 rounded"
              name="guardian_phone"
              value={form.guardian_phone}
              onChange={handleChange}
            />
          </div>
          <div className="flex gap-4 items-center">
            <div>
              <label className="block text-sm mb-1">Photo</label>
              <input
                type="file"
                ref={fileInputPhoto}
                onChange={handlePhoto}
                accept="image/*"
              />
            </div>
            <div>
              <label className="block text-sm mb-1">Birth Certificate</label>
              <input
                type="file"
                ref={fileInputCert}
                onChange={handleCert}
                accept="image/*,application/pdf"
              />
            </div>
          </div>
          {err && <div className="text-red-500">{err}</div>}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white rounded py-2 mt-2"
            disabled={loading}
          >
            {loading ? "Adding..." : "Add Player"}
          </button>
        </form>
      </div>
    </div>
  );
}
