// src/components/Tournament/EditSportModal.js
import React, { useState } from "react";
import axios from "axios";

/**
 * EditSportModal
 * Props:
 *  - open: boolean
 *  - sport: {id, sport_name, team_size, max_teams, rules_url}
 *  - tournamentId: parent tournament
 *  - onClose: close modal
 *  - onUpdated: callback after save
 */
export default function EditSportModal({ open, sport, tournamentId, onClose, onUpdated }) {
  const [sport_name, setSportName] = useState(sport?.sport_name || "");
  const [team_size, setTeamSize] = useState(sport?.team_size || "");
  const [max_teams, setMaxTeams] = useState(sport?.max_teams || "");
  const [rules_url, setRulesUrl] = useState(sport?.rules_url || "");
  const [submitting, setSubmitting] = useState(false);
  const [err, setErr] = useState("");

  if (!open) return null;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    setErr("");
    try {
      await axios.put(
        `http://localhost:5000/api/tournaments/${tournamentId}/sports/${sport.id}`,
        { sport_name, team_size: team_size || null, max_teams: max_teams || null, rules_url },
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      onUpdated && onUpdated();
      onClose();
    } catch (e) {
      setErr(e.response?.data?.error || "Error updating sport.");
    }
    setSubmitting(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black/40">
      <div className="bg-white p-6 rounded-lg shadow-lg min-w-[320px] max-w-[95vw] relative">
        <button className="absolute right-3 top-3 text-xl text-gray-500 hover:text-red-500" onClick={onClose}>
          &times;
        </button>
        <h2 className="text-xl font-bold mb-4 text-athletiq-navy">Edit Sport</h2>
        {err && <div className="text-red-500 mb-2">{err}</div>}
        <form onSubmit={handleSubmit} className="flex flex-col gap-4">
          <div>
            <label className="block font-semibold mb-1">Sport Name<span className="text-red-500">*</span></label>
            <input
              className="border p-2 rounded w-full"
              value={sport_name}
              onChange={e => setSportName(e.target.value)}
              required
              disabled={submitting}
            />
          </div>
          <div>
            <label className="block font-semibold mb-1">Team Size</label>
            <input
              className="border p-2 rounded w-full"
              type="number"
              value={team_size}
              onChange={e => setTeamSize(e.target.value)}
              placeholder="(optional)"
              disabled={submitting}
              min={1}
            />
          </div>
          <div>
            <label className="block font-semibold mb-1">Max Teams</label>
            <input
              className="border p-2 rounded w-full"
              type="number"
              value={max_teams}
              onChange={e => setMaxTeams(e.target.value)}
              placeholder="(optional)"
              disabled={submitting}
              min={1}
            />
          </div>
          <div>
            <label className="block font-semibold mb-1">Rules URL</label>
            <input
              className="border p-2 rounded w-full"
              type="url"
              value={rules_url}
              onChange={e => setRulesUrl(e.target.value)}
              placeholder="https://link-to-rules.pdf"
              disabled={submitting}
            />
          </div>
          <div className="flex justify-end gap-2 mt-4">
            <button
              type="button"
              className="px-4 py-2 rounded bg-gray-200 hover:bg-gray-300 font-semibold"
              onClick={onClose}
              disabled={submitting}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="px-4 py-2 rounded bg-athletiq-green text-white font-semibold hover:bg-green-700"
              disabled={submitting || !sport_name}
            >
              {submitting ? "Saving..." : "Save"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
