import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function SchoolDashboard() {
  const navigate = useNavigate();
  const [school, setSchool] = useState(null);
  const [players, setPlayers] = useState([]);
  const [stats, setStats] = useState({ totalPlayers: 0, pendingPlayers: 0 });
  const [loading, setLoading] = useState(true);

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchData = async () => {
      try {
        // Fetch school info
        const schoolRes = await axios.get(
          "http://localhost:5000/api/schools/me",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setSchool(schoolRes.data);

        // Fetch dashboard summary stats
        const statsRes = await axios.get(
          "http://localhost:5000/api/dashboard/summary",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setStats(statsRes.data);

        // Fetch players for this school
        const playersRes = await axios.get(
          "http://localhost:5000/api/players",
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setPlayers(playersRes.data.players || playersRes.data); // Adapt to your API
      } catch (err) {
        alert("Error loading data. Please log in again.");
        localStorage.removeItem("token");
        navigate("/login");
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [navigate, token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center text-2xl">
        Loading dashboard...
      </div>
    );
  }

  if (!school) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-green-50 flex flex-col items-center py-10 px-2">
      {/* School Info */}
      <div className="bg-white rounded-2xl shadow p-6 mb-6 w-full max-w-2xl flex flex-col md:flex-row items-center">
        <div>
          <h2 className="text-2xl font-bold text-blue-700 mb-2">{school.name}</h2>
          <div className="text-gray-600">{school.address}</div>
          <div className="text-sm text-gray-500 mt-1">
            {school.contact} | {school.email}
          </div>
        </div>
        <button
          className="ml-auto mt-4 md:mt-0 bg-red-500 text-white rounded px-4 py-2 text-sm hover:bg-red-600 transition"
          onClick={handleLogout}
        >
          Logout
        </button>
      </div>

      {/* Dashboard Stats */}
      <div className="flex gap-6 mb-6 w-full max-w-2xl">
        <div className="bg-blue-100 p-4 rounded-xl flex-1 text-center">
          <div className="text-2xl font-bold">{stats.totalPlayers}</div>
          <div className="text-sm text-gray-700">Total Players</div>
        </div>
        <div className="bg-yellow-100 p-4 rounded-xl flex-1 text-center">
          <div className="text-2xl font-bold">{stats.pendingPlayers}</div>
          <div className="text-sm text-gray-700">Pending</div>
        </div>
      </div>

      {/* Players List */}
      <div className="bg-white rounded-2xl shadow p-6 w-full max-w-2xl mb-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-xl font-semibold text-blue-800">Registered Players</h3>
          <button
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition"
            onClick={() => navigate("/register-player")}
          >
            + Register New Player
          </button>
        </div>
        {players.length === 0 ? (
          <div className="text-gray-500">No players registered yet.</div>
        ) : (
          <table className="min-w-full text-left">
            <thead>
              <tr>
                <th className="py-2">ID</th>
                <th className="py-2">Name (Eng)</th>
                <th className="py-2">Name (Nep)</th>
                <th className="py-2">DOB (AD)</th>
                <th className="py-2">DOB (BS)</th>
                <th className="py-2">Gender</th>
                <th className="py-2">Status</th>
              </tr>
            </thead>
            <tbody>
              {players.map((player) => (
                <tr key={player.player_id || player.id} className="border-t">
                  <td className="py-2">{player.player_id || player.id}</td>
                  <td
                    className="py-2 text-blue-600 underline cursor-pointer"
                    onClick={() => navigate(`/player/${player.player_id}`)}
                  >
                    {player.full_name_eng || player.name}
                  </td>
                  <td className="py-2">{player.full_name_nep || ""}</td>
                  <td className="py-2">{player.dob_ad || player.dob}</td>
                  <td className="py-2">{player.dob_bs || ""}</td>
                  <td className="py-2">{player.gender}</td>
                  <td className="py-2">{player.status || "approved"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}
