import React, { useState, useRef, useEffect } from "react";
import axios from "axios";

/**
 * Modal for adding a new school.
 * Props:
 * - open: Boolean, show/hide modal
 * - onClose: Function to close modal
 * - onAdded: Function to call after successful add
 */
export default function AddSchoolModal({ open, onClose, onAdded }) {
  const [form, setForm] = useState({
    name: "",
    address: "",
    email: "",
    is_active: true,
  });
  const [logoFile, setLogoFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");
  const fileInput = useRef();

  // Reset form each time modal opens
  useEffect(() => {
    if (open) {
      setForm({
        name: "",
        address: "",
        email: "",
        is_active: true,
      });
      setLogoFile(null);
      setErr("");
      if (fileInput.current) fileInput.current.value = "";
    }
  }, [open]);

  if (!open) return null;

  // Handle form input change
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm((f) => ({
      ...f,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  // Handle logo upload
  const handleLogo = (e) => {
    setLogoFile(e.target.files[0]);
  };

  // Handle submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErr("");
    try {
      // Validate required
      if (!form.name || !form.address) {
        setErr("Name and address are required.");
        setLoading(false);
        return;
      }
      const data = new FormData();
      data.append("name", form.name);
      data.append("address", form.address);
      data.append("email", form.email);
      data.append("is_active", form.is_active);
      if (logoFile) data.append("logo", logoFile);

      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:5000/api/schools",
        data,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      if (onAdded) onAdded();
      onClose();
    } catch (error) {
      setErr(error?.response?.data?.message || "Add school failed.");
    }
    setLoading(false);
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-30">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-lg relative">
        <button className="absolute top-2 right-3 text-lg" onClick={onClose} type="button">
          &times;
        </button>
        <h2 className="text-xl font-bold mb-4">Add School</h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <div>
            <label className="block text-sm mb-1">School Name</label>
            <input
              className="w-full border p-2 rounded"
              name="name"
              value={form.name}
              onChange={handleChange}
              required
              maxLength={100}
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Address</label>
            <input
              className="w-full border p-2 rounded"
              name="address"
              value={form.address}
              onChange={handleChange}
              required
              maxLength={200}
            />
          </div>
          <div>
            <label className="block text-sm mb-1">Email</label>
            <input
              className="w-full border p-2 rounded"
              name="email"
              type="email"
              value={form.email}
              onChange={handleChange}
              maxLength={100}
              placeholder="(Optional)"
            />
          </div>
          <div className="flex items-center gap-2">
            <input
              type="checkbox"
              name="is_active"
              checked={form.is_active}
              onChange={handleChange}
              className="mr-2"
            />
            <label className="text-sm">Active</label>
          </div>
          <div>
            <label className="block text-sm mb-1">Logo</label>
            <input
              type="file"
              ref={fileInput}
              onChange={handleLogo}
              accept="image/*"
            />
          </div>
          {err && <div className="text-red-500">{err}</div>}
          <button
            type="submit"
            disabled={loading}
            className="w-full bg-blue-600 text-white rounded py-2 mt-2"
          >
            {loading ? "Adding..." : "Add School"}
          </button>
        </form>
      </div>
    </div>
  );
}
