import React from "react";
import { useTranslation } from "react-i18next";

export default function StepSchoolType({ data, updateData, next, back }) {
  const { t } = useTranslation();
  return (
    <div>
      <h2 className="text-xl font-bold mb-4">{t("school_type")}</h2>
      <select
        className="w-full border p-2 mb-4"
        value={data.schoolType || ""}
        onChange={e => updateData({ schoolType: e.target.value })}
        required
      >
        <option value="">{t("select_type")}</option>
        <option value="Public">{t("public")}</option>
        <option value="Private">{t("private")}</option>
        <option value="Community">{t("community")}</option>
      </select>
      <div className="flex justify-between mt-4">
        <button onClick={back} className="text-gray-500">{t("back")}</button>
        <button
          onClick={next}
          className="bg-blue-500 text-white px-4 py-2 rounded"
          disabled={!data.schoolType}
        >
          {t("next")}
        </button>
      </div>
    </div>
  );
}
