// src/components/Tournament/SportCard.js
import React from "react";

/**
 * SportCard
 * Props:
 * - sport: { key, label, icon, gender, ageGroup, weightGroup, teamSize }
 * - onChange: (updatedFields) => void
 * - onDelete: () => void
 */
export default function SportCard({ sport, onChange, onDelete, draggableProps }) {
  return (
    <div
      className="flex items-center gap-3 p-3 bg-athletiq-cream rounded-xl shadow mb-2 border border-athletiq-blue"
      {...draggableProps}
    >
      <span className="text-3xl">{sport.icon}</span>
      <div className="flex-1">
        <div className="font-semibold">{sport.label}</div>
        <div className="flex gap-2 text-xs mt-1">
          <select value={sport.gender} onChange={e => onChange({ gender: e.target.value })}>
            <option value="">Gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="mixed">Mixed</option>
          </select>
          <input
            className="border p-1 rounded"
            type="text"
            placeholder="Age group"
            value={sport.ageGroup || ""}
            onChange={e => onChange({ ageGroup: e.target.value })}
            style={{ width: 80 }}
          />
          <input
            className="border p-1 rounded"
            type="text"
            placeholder="Weight (kg)"
            value={sport.weightGroup || ""}
            onChange={e => onChange({ weightGroup: e.target.value })}
            style={{ width: 80 }}
          />
          <input
            className="border p-1 rounded"
            type="number"
            placeholder="Team size"
            value={sport.teamSize || ""}
            onChange={e => onChange({ teamSize: e.target.value })}
            min={1}
            style={{ width: 70 }}
          />
        </div>
      </div>
      <button className="text-red-600 text-lg font-bold ml-2" onClick={onDelete}>Ã—</button>
      {/* Drag handle can be added for real drag and drop */}
    </div>
  );
}
