import React, { useState } from "react";
import locationData from "../../../data/nepal_location.json";
import { useTranslation } from "react-i18next";

export default function StepTypeAndLocation({ data, updateData, next, back }) {
  const { t } = useTranslation();

  // Local state for dropdowns
  const [provinceId, setProvinceId] = useState(data.province || "");
  const [districtId, setDistrictId] = useState(data.district || "");
  const [municipalityId, setMunicipalityId] = useState(data.municipality || "");

  // Province, District, Municipality filtering
  const provinces = locationData.provinceList;
  const districts = provinceId
    ? provinces.find((p) => String(p.id) === String(provinceId))?.districtList || []
    : [];
  const municipalities = districtId
    ? districts.find((d) => String(d.id) === String(districtId))?.municipalityList || []
    : [];

  // Handlers
  const handleProvince = (val) => {
    setProvinceId(val);
    setDistrictId("");
    setMunicipalityId("");
    updateData({ province: val, district: "", municipality: "" });
  };
  const handleDistrict = (val) => {
    setDistrictId(val);
    setMunicipalityId("");
    updateData({ district: val, municipality: "" });
  };
  const handleMunicipality = (val) => {
    setMunicipalityId(val);
    updateData({ municipality: val });
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">
        {t("school_type_location")}
      </h2>
      {/* School Type */}
      <label className="block mb-1">{t("school_type")}</label>
      <select
        className="w-full border p-2 mb-4"
        value={data.schoolType || ""}
        onChange={e => updateData({ schoolType: e.target.value })}
        required
      >
        <option value="">{t("select_type")}</option>
        <option value="Public">{t("public")}</option>
        <option value="Private">{t("private")}</option>
        <option value="Community">{t("community")}</option>
      </select>
      {/* Province */}
      <label className="block mb-1">{t("province")}</label>
      <select className="w-full border p-2 mb-2" value={provinceId} onChange={e => handleProvince(e.target.value)}>
        <option value="">{t("select_province")}</option>
        {provinces.map((p) => (
          <option key={p.id} value={p.id}>
            {t("lng") === "np" ? (p.name_nep || p.name) : p.name}
          </option>
        ))}
      </select>
      {/* District */}
      <label className="block mb-1">{t("district")}</label>
      <select className="w-full border p-2 mb-2" value={districtId} onChange={e => handleDistrict(e.target.value)} disabled={!provinceId}>
        <option value="">{t("select_district")}</option>
        {districts.map((d) => (
          <option key={d.id} value={d.id}>
            {t("lng") === "np" ? (d.name_nep || d.name) : d.name}
          </option>
        ))}
      </select>
      {/* Municipality/VDC */}
      <label className="block mb-1">{t("municipality")}</label>
      <select className="w-full border p-2 mb-2" value={municipalityId} onChange={e => handleMunicipality(e.target.value)} disabled={!districtId}>
        <option value="">{t("select_municipality")}</option>
        {municipalities.map((m) => (
          <option key={m.id} value={m.id}>
            {t("lng") === "np" ? (m.name_nep || m.name) : m.name}
          </option>
        ))}
      </select>
      {/* Ward & Tole/Street */}
      <input
        className="w-full border p-2 mb-2"
        placeholder={t("ward")}
        value={data.ward || ""}
        onChange={e => updateData({ ward: e.target.value })}
        required
      />
      <input
        className="w-full border p-2 mb-2"
        placeholder={t("tole")}
        value={data.tole || ""}
        onChange={e => updateData({ tole: e.target.value })}
      />
      <div className="flex justify-between mt-4">
        <button onClick={back} className="text-gray-500">
          {t("back")}
        </button>
        <button
          onClick={next}
          className="bg-blue-500 text-white px-4 py-2 rounded"
          disabled={
            !data.schoolType ||
            !provinceId ||
            !districtId ||
            !municipalityId ||
            !data.ward
          }
        >
          {t("next")}
        </button>
      </div>
    </div>
  );
}
