// src/pages/SchoolRegistrationWizard.js

import React, { useState, useEffect } from "react";
import axios from "axios";

const WIZARD_STEPS = [
  "Basic Info",
  "Address",
  "Contact",
  "Logo Upload",
  "Registration Document",
  "Review & Submit"
];

const initialForm = {
  name: "",
  address: "",
  province: "",
  district: "",
  municipality: "",
  ward: "",
  phone: "",
  email: "",
  location_lat: "",
  location_lng: "",
  logo: null,
  registration_doc: null,
};

const getToken = () => localStorage.getItem("token"); // Or your auth method

const SchoolRegistrationWizard = ({ schoolId }) => {
  const [step, setStep] = useState(0);
  const [form, setForm] = useState(initialForm);
  const [status, setStatus] = useState("");
  const [progress, setProgress] = useState(0);
  const [loading, setLoading] = useState(false);
  const [onboardingStatus, setOnboardingStatus] = useState("pending");

  // 1. On mount, if schoolId exists (editing/resume), load existing data
  useEffect(() => {
    if (schoolId) {
      setLoading(true);
      axios
        .get(`/api/schools/${schoolId}`, {
          headers: { Authorization: `Bearer ${getToken()}` },
        })
        .then((res) => {
          const school = res.data;
          setForm((prev) => ({
            ...prev,
            ...school,
            logo: null,
            registration_doc: null,
          }));
          setOnboardingStatus(school.onboarding_status);
          // Set wizard step based on completeness
          let newStep = 0;
          if (school.logo_url) newStep = 4;
          if (school.registration_doc_url) newStep = 5;
          if (school.onboarding_status === "complete") newStep = 5;
          setStep(newStep);
          updateProgress(school);
        })
        .catch((e) => setStatus("Failed to load school data."))
        .finally(() => setLoading(false));
    }
  }, [schoolId]);

  // 2. Progress function
  const updateProgress = (school) => {
    let count = 3; // Basic info, address, contact assumed filled
    if (school.logo_url) count++;
    if (school.registration_doc_url) count++;
    if (school.onboarding_status === "complete") count++;
    setProgress(Math.round((count / WIZARD_STEPS.length) * 100));
  };

  // 3. Form value change handler
  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleFile = (e) => {
    const { name, files } = e.target;
    setForm((prev) => ({ ...prev, [name]: files[0] }));
  };

  // 4. Save/update (PATCH) current step data
  const handleSaveStep = async () => {
    setStatus("");
    setLoading(true);
    const formData = new FormData();
    // Only send relevant fields/files for this step:
    if (step === 0) {
      ["name"].forEach((f) => formData.append(f, form[f]));
    } else if (step === 1) {
      ["address", "province", "district", "municipality", "ward", "location_lat", "location_lng"].forEach((f) => formData.append(f, form[f]));
    } else if (step === 2) {
      ["phone", "email"].forEach((f) => formData.append(f, form[f]));
    } else if (step === 3) {
      if (form.logo) formData.append("logo", form.logo);
    } else if (step === 4) {
      if (form.registration_doc) formData.append("registration_doc", form.registration_doc);
    }
    // PATCH only for existing schools
    try {
      const { data } = await axios.patch(`/api/schools/${schoolId}`, formData, {
        headers: {
          Authorization: `Bearer ${getToken()}`,
          "Content-Type": "multipart/form-data",
        },
      });
      setStatus("Saved!");
      updateProgress(data.school);
      // Move to next step
      if (step < WIZARD_STEPS.length - 1) setStep(step + 1);
      // If last step, mark onboarding_status as complete
      if (step === WIZARD_STEPS.length - 2) {
        await axios.patch(`/api/schools/${schoolId}`, { onboarding_status: "complete" }, {
          headers: { Authorization: `Bearer ${getToken()}` }
        });
        setOnboardingStatus("complete");
      }
    } catch (e) {
      setStatus("âŒ Failed to save step: " + (e.response?.data?.message || e.message));
    }
    setLoading(false);
  };

  // 5. Render current step
  const renderStep = () => {
    switch (step) {
      case 0:
        return (
          <div>
            <label>School Name <input name="name" value={form.name} onChange={handleChange} required /></label>
            <br />
            <button type="button" onClick={handleSaveStep} disabled={loading}>Save & Next</button>
          </div>
        );
      case 1:
        return (
          <div>
            <label>Address <input name="address" value={form.address} onChange={handleChange} required /></label>
            <label>Province <input name="province" value={form.province} onChange={handleChange} /></label>
            <label>District <input name="district" value={form.district} onChange={handleChange} /></label>
            <label>Municipality <input name="municipality" value={form.municipality} onChange={handleChange} /></label>
            <label>Ward <input name="ward" value={form.ward} onChange={handleChange} /></label>
            <button type="button" onClick={handleSaveStep} disabled={loading}>Save & Next</button>
          </div>
        );
      case 2:
        return (
          <div>
            <label>Phone <input name="phone" value={form.phone} onChange={handleChange} required /></label>
            <label>Email <input name="email" value={form.email} onChange={handleChange} /></label>
            <button type="button" onClick={handleSaveStep} disabled={loading}>Save & Next</button>
          </div>
        );
      case 3:
        return (
          <div>
            <label>School Logo
              <input name="logo" type="file" accept="image/*" onChange={handleFile} />
            </label>
            <button type="button" onClick={handleSaveStep} disabled={loading}>Save & Next</button>
          </div>
        );
      case 4:
        return (
          <div>
            <label>Registration Document
              <input name="registration_doc" type="file" accept="image/*,application/pdf" onChange={handleFile} />
            </label>
            <button type="button" onClick={handleSaveStep} disabled={loading}>Save & Next</button>
          </div>
        );
      case 5:
        return (
          <div>
            <h4>Review & Submit</h4>
            <pre>{JSON.stringify(form, null, 2)}</pre>
            {onboardingStatus === "complete" ? <b>ðŸŽ‰ Onboarding Complete!</b> : <i>Submit to complete</i>}
          </div>
        );
      default:
        return null;
    }
  };

  // 6. Progress UI
  return (
    <div style={{ maxWidth: 500, margin: "auto", padding: 20 }}>
      <h2>School Registration Wizard</h2>
      <div>
        <b>Step {step + 1} of {WIZARD_STEPS.length}: {WIZARD_STEPS[step]}</b>
        <progress value={progress} max="100" style={{ width: "100%" }} />
        <span> {progress}%</span>
      </div>
      <div style={{ margin: "1em 0" }}>{renderStep()}</div>
      {step > 0 && step < WIZARD_STEPS.length - 1 && (
        <button onClick={() => setStep(step - 1)} disabled={loading}>Back</button>
      )}
      <div style={{ margin: "1em 0", color: status.startsWith("âŒ") ? "red" : "green" }}>{status}</div>
      {onboardingStatus === "complete" && <div style={{ color: "green" }}>Your school onboarding is 100% complete!</div>}
    </div>
  );
};

export default SchoolRegistrationWizard;

/**
 * ONBOARDING NOTES:
 * - On mount, fetches school data if editing/resuming (schoolId prop).
 * - PATCHes fields/files step-by-step.
 * - Shows progress and disables fields if onboarding is complete.
 * - Use in dashboard or as main onboarding page for school admins.
 * - Update: dashboard can call GET /api/schools/:id for status/progress at any time.
 */
