import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function SchoolDashboard() {
  const [school, setSchool] = useState(null);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSchool = async () => {
      try {
        const token = localStorage.getItem('token');
        // Get the school linked to the current admin
        // (in a real app, you'd fetch user's school_id from /me or user object)
        // For now, you may need to store school_id in localStorage after onboarding:
        let schoolId = localStorage.getItem('school_id');
        // If school_id not in localStorage, get from user info (optional)
        if (!schoolId) {
          const user = JSON.parse(localStorage.getItem('user'));
          // You might want to fetch /api/users/me to get their linked school_id
          // Here, let's just handle missing schoolId for now
        }
        if (!schoolId) {
          setErr('No school found for this admin.');
          setLoading(false);
          return;
        }

        const res = await axios.get(
          `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api/schools'}/${schoolId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setSchool(res.data);
        setLoading(false);
      } catch (error) {
        setErr('Failed to load school data.');
        setLoading(false);
      }
    };
    fetchSchool();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (err) return <div className="text-red-500">{err}</div>;

  // Simple progress bar calculation
  const progressFields = [
    "name", "address", "province", "district", "municipality", "ward", "phone", "email", "logo_url", "registration_doc_url"
  ];
  const completed = progressFields.filter(field => !!school[field]);
  const percent = Math.round((completed.length / progressFields.length) * 100);

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white rounded shadow">
      <h2 className="text-2xl font-bold mb-4">School Dashboard</h2>
      <div className="mb-4">
        <span className="font-semibold">School Name: </span>{school.name}<br />
        <span className="font-semibold">Onboarding Status: </span>
        <span className={school.onboarding_status === "complete" ? "text-green-600" : "text-yellow-600"}>
          {school.onboarding_status || "pending"}
        </span>
      </div>
      <div className="mb-6">
        <div className="mb-2 font-semibold">Onboarding Progress:</div>
        <div className="w-full h-4 bg-gray-200 rounded mb-2">
          <div
            className="h-4 bg-green-400 rounded"
            style={{ width: `${percent}%` }}
          ></div>
        </div>
        <div>{percent}% complete</div>
      </div>
      {school.onboarding_status !== "complete" && (
        <button
          className="py-2 px-4 bg-blue-500 text-white rounded"
          onClick={() => navigate("/school-onboarding")}
        >
          Resume Onboarding
        </button>
      )}
      {school.onboarding_status === "complete" && (
        <div className="text-green-600 font-semibold mb-4">
          🎉 Onboarding complete! You can now access all features.
        </div>
      )}
      <div className="mt-6">
        <div><span className="font-semibold">Address:</span> {school.address}</div>
        <div><span className="font-semibold">Contact:</span> {school.phone}</div>
        <div><span className="font-semibold">Email:</span> {school.email}</div>
        {school.logo_url && (
          <div className="mt-4">
            <span className="font-semibold">Logo:</span>
            <img src={`/uploads/${school.logo_url}`} alt="logo" className="w-24 h-24 object-contain" />
          </div>
        )}
        {school.registration_doc_url && (
          <div className="mt-4">
            <span className="font-semibold">Registration Document:</span>
            <a
              href={`/uploads/${school.registration_doc_url}`}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-700 underline"
            >
              View Document
            </a>
          </div>
        )}
      </div>
    </div>
  );
}
