/**
 * ATHLETIQ – Player Registration (Dual Calendar, Bilingual)
 * 
 * Features:
 * - Users can enter full name in English, Nepali, or both (backend will auto-translate).
 * - DOB is entered in AD (required). User can also pick BS (Bikram Sambat) using a Nepali date picker.
 * - Files for photo and birth certificate are uploaded.
 * - No backend/Node imports! Pure React code for web/mobile.
 * 
 * Onboarding:
 * - Install "nepali-datepicker-reactjs" for BS picker:
 *     npm install nepali-datepicker-reactjs
 * - Send only AD DOB to backend for storage/processing.
 * - Display BS DOB on frontend if user wants for clarity (not sent to backend unless you want for display).
 */

import React, { useState } from "react";
import axios from "axios";
import { NepaliDatePicker } from "nepali-datepicker-reactjs";
import "nepali-datepicker-reactjs/dist/index.css";

const initialState = {
  full_name: "",
  full_name_eng: "",
  full_name_nep: "",
  dob_ad: "",
  gender: "",
  father_name: "",
  mother_name: "",
  guardian_phone: "",
  guardian_email: "",
  address: "",
};

const PlayerRegister = () => {
  const [form, setForm] = useState(initialState);
  const [photo, setPhoto] = useState(null);
  const [birthCertificate, setBirthCertificate] = useState(null);
  const [dobBs, setDobBs] = useState(""); // BS for UI only
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  // For date picker: update BS and AD when BS is picked
  const handleNepaliDate = ({ bsDate, adDate }) => {
    setDobBs(bsDate);
    setForm({ ...form, dob_ad: adDate });
  };

  // Handles all text/input changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Handles file input
  const handleFile = (e) => {
    if (e.target.name === "photo") setPhoto(e.target.files[0]);
    if (e.target.name === "birthCertificate") setBirthCertificate(e.target.files[0]);
  };

  // Handles form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("");
    setLoading(true);
    const data = new FormData();
    Object.entries(form).forEach(([key, value]) => data.append(key, value));
    if (photo) data.append("photo", photo);
    if (birthCertificate) data.append("birthCertificate", birthCertificate);

    try {
      await axios.post("/api/players/register", data, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setStatus("✅ Player registered successfully!");
      setForm(initialState);
      setDobBs("");
      setPhoto(null);
      setBirthCertificate(null);
    } catch (err) {
      setStatus(
        "❌ " +
          (err.response?.data?.message ||
            err.message ||
            "Registration failed. Try again.")
      );
    }
    setLoading(false);
  };

  return (
    <div style={{ maxWidth: 480, margin: "2rem auto", padding: "2rem", border: "1px solid #eee", borderRadius: 12, background: "#fff" }}>
      <h2>Player Registration</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <label>
          Full Name (English or Nepali) <span style={{color:"red"}}>*</span><br />
          <input
            type="text"
            name="full_name"
            value={form.full_name}
            onChange={handleChange}
            required={!form.full_name_eng && !form.full_name_nep}
            placeholder="Full Name"
          />
        </label>
        <br />
        <label>
          Full Name (English)
          <input
            type="text"
            name="full_name_eng"
            value={form.full_name_eng}
            onChange={handleChange}
            placeholder="(If available)"
          />
        </label>
        <br />
        <label>
          Full Name (Nepali)
          <input
            type="text"
            name="full_name_nep"
            value={form.full_name_nep}
            onChange={handleChange}
            placeholder="(यदि उपलब्ध छ भने)"
          />
        </label>
        <br />
        <label>
          Date of Birth (AD) <span style={{color:"red"}}>*</span><br />
          <input
            type="date"
            name="dob_ad"
            value={form.dob_ad}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          (Optional) Date of Birth (BS)
          <NepaliDatePicker
            inputClassName="form-control"
            value={dobBs}
            onChange={handleNepaliDate}
            options={{ calenderLocale: "ne", valueLocale: "en" }}
          />
        </label>
        <br />
        <label>
          Gender <span style={{color:"red"}}>*</span>
          <select name="gender" value={form.gender} onChange={handleChange} required>
            <option value="">--Select--</option>
            <option value="M">Male/छोरा</option>
            <option value="F">Female/छोरी</option>
            <option value="O">Other/अन्य</option>
          </select>
        </label>
        <br />
        <label>
          Father Name <span style={{color:"red"}}>*</span>
          <input
            type="text"
            name="father_name"
            value={form.father_name}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Mother Name
          <input
            type="text"
            name="mother_name"
            value={form.mother_name}
            onChange={handleChange}
            placeholder="(If available)"
          />
        </label>
        <br />
        <label>
          Guardian Phone <span style={{color:"red"}}>*</span>
          <input
            type="tel"
            name="guardian_phone"
            value={form.guardian_phone}
            onChange={handleChange}
            required
            pattern="^[0-9]{10}$"
            placeholder="98XXXXXXXX"
          />
        </label>
        <br />
        <label>
          Guardian Email
          <input
            type="email"
            name="guardian_email"
            value={form.guardian_email}
            onChange={handleChange}
            placeholder="(If available)"
          />
        </label>
        <br />
        <label>
          Address
          <input
            type="text"
            name="address"
            value={form.address}
            onChange={handleChange}
            placeholder="Permanent Address"
          />
        </label>
        <br />
        <label>
          Photo (passport size) <span style={{color:"red"}}>*</span>
          <input
            type="file"
            name="photo"
            accept="image/*"
            onChange={handleFile}
            required
          />
        </label>
        <br />
        <label>
          Birth Certificate <span style={{color:"red"}}>*</span>
          <input
            type="file"
            name="birthCertificate"
            accept="image/*,application/pdf"
            onChange={handleFile}
            required
          />
        </label>
        <br /><br />
        <button type="submit" disabled={loading}>
          {loading ? "Registering..." : "Register Player"}
        </button>
      </form>
      <div style={{marginTop: "1rem"}}>
        <span style={{color: "#555", fontSize: "0.95em"}}>
          {dobBs && `BS Date: ${dobBs} (auto-converts to AD for backend)`}
        </span>
        {status && (
          <div style={{marginTop: "1rem", fontWeight: "bold", color: status.startsWith("✅") ? "green" : "red"}}>
            {status}
          </div>
        )}
      </div>
    </div>
  );
};

export default PlayerRegister;
