// src/components/Tournament/StepSports.jsx
import { useState } from "react";
import sportsList from "../../data/sportsList";
import * as Icons from "lucide-react";
import { motion } from "framer-motion";

export default function StepSports({ value = [], onChange, onNext }) {
  const [search, setSearch] = useState("");
  const [selected, setSelected] = useState(value);

  // Filtered sports for search
  const filtered = sportsList.filter(s =>
    s.name.toLowerCase().includes(search.toLowerCase())
  );

  // Add sport (can add multiple times, but each with a unique key)
  const handleAdd = (sport) => {
    const newEntry = { ...sport, key: `${sport.id}-${Date.now()}-${Math.random()}` };
    setSelected(prev => [...prev, newEntry]);
    onChange([...selected, newEntry]);
  };

  // Remove sport by key
  const handleRemove = (key) => {
    const filtered = selected.filter(s => s.key !== key);
    setSelected(filtered);
    onChange(filtered);
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-3 text-athletiq-navy">Choose Sports</h2>
      <input
        className="border p-2 rounded w-full mb-4"
        placeholder="Search sports..."
        value={search}
        onChange={e => setSearch(e.target.value)}
      />
      <div className="grid grid-cols-4 gap-4 mb-6">
        {filtered.map(sport => {
          const Icon = Icons[sport.icon] || Icons.Trophy;
          return (
            <motion.div
              key={sport.id}
              whileHover={{ scale: 1.09 }}
              whileTap={{ scale: 0.97 }}
              className="bg-athletiq-cream border border-athletiq-blue rounded-lg shadow p-3 flex flex-col items-center cursor-pointer hover:bg-athletiq-blue/10 transition"
              onClick={() => handleAdd(sport)}
              title={sport.name}
            >
              <Icon className="w-8 h-8 text-athletiq-blue mb-2" />
              <span className="text-athletiq-navy font-semibold text-xs text-center">{sport.name}</span>
            </motion.div>
          );
        })}
      </div>

      <div>
        <div className="font-semibold mb-2 text-athletiq-navy">Selected:</div>
        <div className="flex flex-wrap gap-2 mb-4 min-h-[38px]">
          {selected.map((s, idx) => (
            <span
              key={s.key}
              className="bg-athletiq-green text-white px-3 py-1 rounded-lg flex items-center gap-1"
            >
              {s.name}
              <button
                className="ml-1 text-xs font-bold hover:text-red-200"
                onClick={() => handleRemove(s.key)}
                aria-label="Remove"
              >âœ•</button>
            </span>
          ))}
        </div>
      </div>

      <div className="flex justify-end mt-4">
        <button
          className="px-5 py-2 rounded bg-athletiq-green text-white font-semibold"
          disabled={selected.length === 0}
          onClick={onNext}
        >
          Next
        </button>
      </div>
    </div>
  );
}
