import React, { useState } from "react";
import { useTranslation } from "react-i18next";

export default function StepReviewSubmit({ data, back, onSubmit }) {
  const { t } = useTranslation();
  const [submitting, setSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);
    await onSubmit();
    setSubmitting(false);
  };

  // Display all fields for review (customize as you wish)
  return (
    <form onSubmit={handleSubmit}>
      <h2 className="text-xl font-bold mb-2">{t("review_submit")}</h2>
      <p className="mb-4 text-gray-600">{t("review_msg")}</p>
      <div className="bg-gray-100 rounded p-3 mb-4 text-sm">
        <strong>{t("school_type")}</strong>: {data.schoolType}<br />
        <strong>{t("province")}</strong>: {data.province}<br />
        <strong>{t("district")}</strong>: {data.district}<br />
        <strong>{t("municipality")}</strong>: {data.municipality}<br />
        <strong>{t("ward")}</strong>: {data.ward}<br />
        <strong>{t("tole")}</strong>: {data.tole}<br />
        <strong>{t("mobile")}</strong>: {data.mobile}<br />
        <strong>{t("landline")}</strong>: {data.landline}<br />
        <strong>{t("email")}</strong>: {data.email}<br />
        <strong>{t("admin_name")}</strong>: {data.adminName}<br />
        <strong>{t("admin_email")}</strong>: {data.adminEmail}<br />
        {/* Files: */}
        <strong>{t("school_logo")}</strong>: {data.schoolLogo?.name || "-"}<br />
        <strong>{t("official_doc")}</strong>: {data.officialDoc?.name || "-"}<br />
      </div>
      <div className="flex justify-between mt-4">
        <button type="button" onClick={back} className="text-gray-500">
          {t("back")}
        </button>
        <button
          type="submit"
          className="bg-green-600 text-white px-4 py-2 rounded"
          disabled={submitting}
        >
          {submitting ? t("submitting") || "Submitting..." : t("submit")}
        </button>
      </div>
    </form>
  );
}
