import React, { useRef } from "react";
import { useTranslation } from "react-i18next";
import { useJsApiLoader, Autocomplete } from "@react-google-maps/api";

const GOOGLE_MAPS_API_KEY = "AIzaSyAmXGwHvwU-idbaDJYDyCP1xb5flacuKc0"; // replace this!

export default function SchoolAddressGoogle({ data, updateData, next, back }) {
  const { t } = useTranslation();
  const autocompleteRef = useRef(null);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: GOOGLE_MAPS_API_KEY,
    libraries: ["places"]
  });

  const handlePlaceChanged = () => {
    const place = autocompleteRef.current.getPlace();
    // Parse address components as needed
    const components = place.address_components || [];
    const getComp = type => components.find(c => c.types.includes(type))?.long_name || "";
    updateData({
      address: place.formatted_address || "",
      country: getComp("country"),
      state: getComp("administrative_area_level_1"),
      city: getComp("locality") || getComp("administrative_area_level_2"),
      postalCode: getComp("postal_code"),
      lat: place.geometry?.location?.lat() || "",
      lng: place.geometry?.location?.lng() || ""
    });
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">{t("school_type_location")}</h2>
      <label className="block mb-1">{t("address") || "School Address"}</label>
      {isLoaded && (
        <Autocomplete
          onLoad={autoC => (autocompleteRef.current = autoC)}
          onPlaceChanged={handlePlaceChanged}
        >
          <input
            type="text"
            placeholder={t("address") || "Search or enter address"}
            className="w-full border p-2 mb-2"
            defaultValue={data.address || ""}
          />
        </Autocomplete>
      )}
      <div className="mb-2">
        <small>
          {t("country")}: {data.country || "-"}
          <br />
          {t("state")}: {data.state || "-"}
          <br />
          {t("city")}: {data.city || "-"}
          <br />
          {t("postalCode")}: {data.postalCode || "-"}
          <br />
          Lat: {data.lat || "-"}, Lng: {data.lng || "-"}
        </small>
      </div>
      <div className="flex justify-between mt-4">
        <button onClick={back} className="text-gray-500">{t("back")}</button>
        <button
          onClick={next}
          className="bg-blue-500 text-white px-4 py-2 rounded"
          disabled={!data.address}
        >
          {t("next")}
        </button>
      </div>
    </div>
  );
}
