import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setErr('');
    try {
      const url = `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api/auth/login'}`;
      const res = await axios.post(url, { email, password });
      // Save JWT & user info in localStorage for auth
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      // Redirect by role
      if (res.data.user.role === 'school_admin') {
        navigate('/school-dashboard');
      } else if (res.data.user.role === 'player') {
        navigate('/player-dashboard');
      } else {
        navigate('/dashboard');
      }
    } catch (error) {
      setErr(error?.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <form className="bg-white p-8 rounded shadow w-full max-w-md" onSubmit={handleLogin}>
        <h2 className="text-xl font-bold mb-4">Login</h2>
        <input
          className="w-full border p-2 mb-2"
          placeholder="Email"
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <input
          className="w-full border p-2 mb-2"
          placeholder="Password"
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        {err && <div className="text-red-500 mb-2">{err}</div>}
        <button className="w-full py-2 bg-blue-500 text-white rounded" type="submit">Login</button>
      </form>
    </div>
  );
}
