import React, { useState } from "react";
import axios from "axios";

/**
 * Props:
 * - open: boolean
 * - onClose: function
 * - school: school object (must have id, name)
 * - onChanged: callback after password changed
 */
export default function ChangeAdminPasswordModal({ open, onClose, school, onChanged }) {
  const [password, setPassword] = useState("");
  const [err, setErr] = useState("");
  const [loading, setLoading] = useState(false);
  const token = localStorage.getItem("token");

  if (!open) return null;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErr("");
    setLoading(true);
    try {
      await axios.patch(
        `http://localhost:5000/api/schools/${school.id}/admin-password`,
        { password },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setLoading(false);
      setPassword("");
      if (onChanged) onChanged();
      alert("Password updated successfully!");
      onClose();
    } catch (error) {
      setErr(error?.response?.data?.message || "Failed to update password.");
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 min-w-[350px] shadow-lg">
        <h2 className="text-xl font-bold mb-3">
          Change Admin Password <br /> <span className="text-base font-normal">({school.name})</span>
        </h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <div>
            <label className="block text-sm mb-1 font-medium">New Password</label>
            <input
              type="password"
              className="w-full border rounded p-2"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
          </div>
          {err && <div className="text-red-500">{err}</div>}
          <div className="flex justify-end gap-2">
            <button
              type="button"
              className="bg-gray-200 px-4 py-1 rounded"
              onClick={onClose}
              disabled={loading}
            >
              Cancel
            </button>
            <button
              type="submit"
              className="bg-athletiq-green text-white px-4 py-1 rounded"
              disabled={loading || !password}
            >
              {loading ? "Updating..." : "Change"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
