import React, { useEffect, useState } from "react";
import axios from "axios";
import EditSchoolModal from "../components/EditSchoolModal";
import AddPlayerModal from "../components/AddPlayerModal";
import EditPlayerModal from "../components/EditPlayerModal";
import ViewPlayerModal from "../components/ViewPlayerModal"; // If you have one

export default function SchoolDashboard() {
  // 1. Get user from localStorage (school admin must be logged in)
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  const token = localStorage.getItem("token");

  // 2. State for school, players, loading, error, modals
  const [school, setSchool] = useState(null);
  const [players, setPlayers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [err, setErr] = useState("");
  const [editSchoolOpen, setEditSchoolOpen] = useState(false);
  const [addPlayerOpen, setAddPlayerOpen] = useState(false);
  const [editPlayer, setEditPlayer] = useState(null);
  const [viewPlayer, setViewPlayer] = useState(null);

  // 3. Fetch school + players for this school admin
  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      setErr("");
      try {
        // a. Get school profile for this admin (token auto-includes user ID)
        const schoolRes = await axios.get("http://localhost:5000/api/schools/me", {
          headers: { Authorization: `Bearer ${token}` }
        });
        setSchool(schoolRes.data.school);

        // b. Get all players for this school (use /api/players?school_id=...)
        const playersRes = await axios.get(
          `http://localhost:5000/api/players?school_id=${schoolRes.data.school.id}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setPlayers(playersRes.data.players || []);
      } catch (error) {
        setErr(error?.response?.data?.message || "Failed to load school or players.");
      }
      setLoading(false);
    }
    if (user.role === "school_admin" && token) fetchData();
    // eslint-disable-next-line
  }, [token]);

  // 4. Handle updates
  const handleSchoolUpdated = (updatedSchool) => {
    setSchool(updatedSchool || school);
    setEditSchoolOpen(false);
  };

  const handlePlayerAdded = () => {
    // Refetch players after adding
    if (school) {
      axios
        .get(`http://localhost:5000/api/players?school_id=${school.id}`, {
          headers: { Authorization: `Bearer ${token}` }
        })
        .then((res) => setPlayers(res.data.players || []))
        .catch(() => {});
    }
    setAddPlayerOpen(false);
  };

  const handlePlayerUpdated = () => {
    if (school) {
      axios
        .get(`http://localhost:5000/api/players?school_id=${school.id}`, {
          headers: { Authorization: `Bearer ${token}` }
        })
        .then((res) => setPlayers(res.data.players || []))
        .catch(() => {});
    }
    setEditPlayer(null);
  };

  // 5. Delete player
  const handleDeletePlayer = async (playerId) => {
    if (!window.confirm("Delete this player?")) return;
    try {
      await axios.delete(`http://localhost:5000/api/players/${playerId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPlayers((prev) => prev.filter((p) => p.id !== playerId));
    } catch (error) {
      alert("Delete failed.");
    }
  };

  // 6. UI (school + player roster)
  if (user.role !== "school_admin") {
    return (
      <div className="p-8">
        <h1 className="text-2xl font-bold mb-2">Unauthorized</h1>
        <p>You must be logged in as a school admin to access this page.</p>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-5xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">School Dashboard</h1>
      <div className="mb-2">Welcome, <span className="font-semibold">{user.full_name || user.email}</span>!</div>
      <div className="mb-6">
        <span className="font-semibold">School:</span>{" "}
        {school ? school.name : "Loading..."} (ID: {school?.id})
        {school && (
          <button
            className="ml-3 px-3 py-1 bg-blue-500 text-white rounded"
            onClick={() => setEditSchoolOpen(true)}
          >
            Edit School
          </button>
        )}
      </div>

      {err && <div className="text-red-500 mb-4">{err}</div>}
      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          {/* Player Section */}
          <div className="flex items-center justify-between mb-2">
            <h2 className="text-xl font-semibold">Your Players</h2>
            <button
              className="px-4 py-2 bg-green-600 text-white rounded"
              onClick={() => setAddPlayerOpen(true)}
            >
              Add Player
            </button>
          </div>
          <table className="min-w-full bg-white rounded shadow text-sm">
            <thead>
              <tr className="bg-blue-100">
                <th className="p-2 text-left">Name</th>
                <th className="p-2 text-left">DOB</th>
                <th className="p-2 text-left">Phone</th>
                <th className="p-2 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {players.length === 0 && (
                <tr>
                  <td className="p-2" colSpan={4}>
                    No players found.
                  </td>
                </tr>
              )}
              {players.map((p) => (
                <tr key={p.id}>
                  <td className="p-2">{p.full_name}</td>
                  <td className="p-2">{p.dob}</td>
                  <td className="p-2">{p.guardian_phone}</td>
                  <td className="p-2 flex gap-2">
                    <button
                      className="bg-blue-500 text-white px-2 py-1 rounded"
                      onClick={() => setViewPlayer(p)}
                    >
                      View
                    </button>
                    <button
                      className="bg-green-500 text-white px-2 py-1 rounded"
                      onClick={() => setEditPlayer(p)}
                    >
                      Edit
                    </button>
                    <button
                      className="bg-red-500 text-white px-2 py-1 rounded"
                      onClick={() => handleDeletePlayer(p.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* Edit School Modal */}
      {editSchoolOpen && school && (
        <EditSchoolModal
          open={editSchoolOpen}
          school={school}
          onClose={() => setEditSchoolOpen(false)}
          onUpdated={handleSchoolUpdated}
        />
      )}

      {/* Add Player Modal */}
      {addPlayerOpen && (
        <AddPlayerModal
          open={addPlayerOpen}
          onClose={() => setAddPlayerOpen(false)}
          onAdded={handlePlayerAdded}
          schools={[school]} // Pass only THIS school
        />
      )}

      {/* Edit Player Modal */}
      {editPlayer && (
        <EditPlayerModal
          open={!!editPlayer}
          onClose={() => setEditPlayer(null)}
          player={editPlayer}
          schools={[school]}
          onUpdated={handlePlayerUpdated}
        />
      )}

      {/* View Player Modal (optional) */}
      {viewPlayer && (
        <ViewPlayerModal
          open={!!viewPlayer}
          onClose={() => setViewPlayer(null)}
          player={viewPlayer}
          schools={[school]}
        />
      )}
    </div>
  );
}
