import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [err, setErr] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErr("");
    setLoading(true);
    try {
      const url =
        process.env.REACT_APP_API_URL || "http://localhost:5000/api/auth/login";
      const res = await axios.post(url, { email, password });
      // Store JWT and user info in localStorage for route protection
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      setLoading(false);

      // Redirect based on role
      const role = res.data.user?.role;
      if (role === "school_admin") navigate("/school-dashboard");
      else if (role === "super_admin") navigate("/admin");
      else if (role === "coach") navigate("/player-list");
      else navigate("/");
    } catch (error) {
      setErr(error?.response?.data?.message || "Login failed.");
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <form
        className="bg-white p-8 rounded shadow w-full max-w-md"
        onSubmit={handleSubmit}
      >
        <h2 className="text-xl font-bold mb-4">Login</h2>
        <input
          className="w-full border p-2 mb-2"
          placeholder="Email"
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <input
          className="w-full border p-2 mb-2"
          placeholder="Password"
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        {err && <div className="text-red-500 mb-2">{err}</div>}
        <button
          className="w-full py-2 bg-blue-600 text-white rounded font-semibold"
          type="submit"
          disabled={loading}
        >
          {loading ? "Logging in..." : "Login"}
        </button>
        <div className="mt-4 text-sm">
          Donâ€™t have an account?{" "}
          <span
            className="text-blue-600 underline cursor-pointer"
            onClick={() => navigate("/register")}
          >
            Register here
          </span>
        </div>
      </form>
    </div>
  );
}
