import React, { useEffect, useState } from "react";
import axios from "axios";
import DashboardLayout from "../components/DashboardLayout";
// ... (all your modal imports)

export default function AdminDashboard() {
  // State variables
  const [schools, setSchools] = useState([]);
  const [players, setPlayers] = useState([]);
  const [tournaments, setTournaments] = useState([]); // <--- NEW
  const [view, setView] = useState("schools");
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ schools: 0, players: 0, tournaments: 0 });
  const [err, setErr] = useState("");
  // ... (all your other modals and UI state as before)

  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user") || "{}");

  // Fetch logic for schools, players, tournaments
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setErr("");
      try {
        // Schools, players
        if (view === "schools" || view === "players" || view === "stats") {
          const [schoolsRes, playersRes] = await Promise.all([
            axios.get("http://localhost:5000/api/schools", {
              headers: { Authorization: `Bearer ${token}` },
            }),
            axios.get(
              `http://localhost:5000/api/players${
                user.role === "super_admin" && selectedSchoolId
                  ? `?school_id=${selectedSchoolId}`
                  : ""
              }`,
              { headers: { Authorization: `Bearer ${token}` } }
            ),
          ]);
          setSchools(schoolsRes.data.schools || []);
          setPlayers(playersRes.data.players || []);
          setStats({
            schools: schoolsRes.data.schools?.length || 0,
            players: playersRes.data.players?.length || 0,
            tournaments: stats.tournaments // keep prev
          });
        }
        // Tournaments
        if (view === "tournaments") {
          const tournamentsRes = await axios.get(
            "http://localhost:5000/api/tournaments",
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setTournaments(tournamentsRes.data || []);
          setStats((prev) => ({
            ...prev,
            tournaments: tournamentsRes.data?.length || 0
          }));
        }
        setSelectedPlayerIds([]);
      } catch (error) {
        setErr(error?.response?.data?.message || "Error loading data.");
      }
      setLoading(false);
    };
    fetchData();
    // eslint-disable-next-line
  }, [token, selectedSchoolId, view]);

  // ... (keep all reload helpers, delete handlers, sidebarLinks as you have)
  const sidebarLinks = [
    { label: "Schools", icon: "ðŸ«", onClick: () => setView("schools"), active: view === "schools" },
    { label: "Players", icon: "ðŸ§‘â€ðŸŽ“", onClick: () => setView("players"), active: view === "players" },
    { label: "Tournaments", icon: "ðŸ†", onClick: () => setView("tournaments"), active: view === "tournaments" },
    { label: "Platform Stats", icon: "ðŸ“Š", onClick: () => setView("stats"), active: view === "stats" },
  ];

  // ... (checkbox logic, modals, etc as before)

  return (
    <DashboardLayout user={user} onLogout={() => {
      localStorage.removeItem("token");
      localStorage.removeItem("user");
      window.location.href = "/login";
    }} sidebarLinks={sidebarLinks}>
      <div className="flex flex-col gap-6 w-full">
        <h1 className="text-3xl font-extrabold text-athletiq-navy mb-2">Super Admin Dashboard</h1>
        {err && <div className="text-red-500 mb-4">{err}</div>}
        {loading ? (
          <div>Loading...</div>
        ) : (
          <>
            {/* -------- Schools View -------- */}
            {view === "schools" && (
              // ... your existing schools table and modals
              <div> {/* ... Schools Table ... */} </div>
            )}
            {/* -------- Players View -------- */}
            {view === "players" && (
              // ... your existing players table and modals
              <div> {/* ... Players Table ... */} </div>
            )}
            {/* -------- Tournaments View -------- */}
            {view === "tournaments" && (
              <div>
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-xl font-bold text-athletiq-navy">All Tournaments</h2>
                  {/* You can add an "Add Tournament" modal/button here */}
                </div>
                {tournaments.length === 0 ? (
                  <div>No tournaments found.</div>
                ) : (
                  <div className="overflow-x-auto bg-white rounded-lg shadow-md">
                    <table className="min-w-full text-sm">
                      <thead>
                        <tr className="bg-athletiq-blue text-white">
                          <th className="p-2 text-left">Name</th>
                          <th className="p-2 text-left">Code</th>
                          <th className="p-2 text-left">Sports</th>
                          <th className="p-2 text-left">Start Date</th>
                          <th className="p-2 text-left">Status</th>
                          <th className="p-2 text-left">Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {tournaments.map((t) => (
                          <tr key={t.id} className="border-b last:border-none">
                            <td className="p-2">{t.name}</td>
                            <td className="p-2">{t.code}</td>
                            <td className="p-2">
                              {(Array.isArray(t.sports)
                                ? t.sports.join(", ")
                                : JSON.parse(t.sports || "[]").join(", ")
                              ) || "-"}
                            </td>
                            <td className="p-2">{t.start_date?.slice(0, 10)}</td>
                            <td className="p-2">{t.status || "Active"}</td>
                            <td className="p-2">
                              {/* Add Edit/View/Delete buttons here if needed */}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            )}
            {/* -------- Stats View -------- */}
            {view === "stats" && (
              <div>
                <h2 className="text-xl font-bold text-athletiq-navy mb-4">Platform Stats</h2>
                <div className="flex gap-8">
                  <div className="bg-athletiq-blue px-6 py-4 rounded text-center shadow text-white">
                    <div className="text-3xl font-bold">{stats.schools}</div>
                    <div>Schools</div>
                  </div>
                  <div className="bg-athletiq-green px-6 py-4 rounded text-center shadow text-white">
                    <div className="text-3xl font-bold">{stats.players}</div>
                    <div>Players</div>
                  </div>
                  <div className="bg-athletiq-blue px-6 py-4 rounded text-center shadow text-white">
                    <div className="text-3xl font-bold">{stats.tournaments}</div>
                    <div>Tournaments</div>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </DashboardLayout>
  );
}
