// src/components/DashboardLayout.js

import React from "react";
import Sidebar from "./Sidebar";
import Topbar from "./Topbar";

/**
 * DashboardLayout
 * Wraps all dashboard pages with sidebar, topbar, and content area.
 * Props:
 *  - user: logged in user object
 *  - school: (optional) school object
 *  - onLogout: logout handler
 *  - children: page content
 *  - sidebarLinks: optional override for sidebar items
 */
export default function DashboardLayout({
  user,
  school,
  onLogout,
  children,
  sidebarLinks,
}) {
  return (
    <div className="flex h-screen bg-athletiq-cream overflow-hidden">
      {/* Sidebar */}
      <Sidebar user={user} school={school} sidebarLinks={sidebarLinks} />
      {/* Main content area */}
      <div className="flex-1 flex flex-col overflow-auto">
        {/* Topbar */}
        <Topbar user={user} school={school} onLogout={onLogout} />
        {/* Page Content */}
        <main className="flex-1 p-6 bg-athletiq-cream overflow-y-auto">
          {children}
        </main>
      </div>
    </div>
  );
}

/*
ONBOARDING NOTES:
- DashboardLayout.js wraps ALL dashboard screens (school, super admin, etc).
- Combines Sidebar, Topbar, and children (the page content).
- Prop sidebarLinks: for customizing the sidebar menu per dashboard.
- Use as a wrapper in each dashboard page, passing user, school, onLogout, etc.
- Uses Tailwind and ATHLETIQ color theme.
*/
