import React, { useRef } from "react";
import { useTranslation } from "react-i18next";

export default function StepUploads({ data, updateData, next, back }) {
  const { t } = useTranslation();
  const logoInputRef = useRef();
  const docInputRef = useRef();

  // File handlers
  const handleLogoChange = e => {
    updateData({ schoolLogo: e.target.files[0] });
  };
  const handleDocChange = e => {
    updateData({ officialDoc: e.target.files[0] });
  };

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">{t("upload_documents")}</h2>
      <label className="block mb-1">{t("school_logo")}</label>
      <input
        ref={logoInputRef}
        type="file"
        accept="image/*"
        className="mb-3"
        onChange={handleLogoChange}
      />
      <label className="block mb-1">{t("official_doc")}</label>
      <input
        ref={docInputRef}
        type="file"
        accept="image/*,.pdf"
        className="mb-3"
        onChange={handleDocChange}
        required
      />
      <div className="flex justify-between mt-4">
        <button onClick={back} className="text-gray-500">
          {t("back")}
        </button>
        <button
          onClick={next}
          className="bg-blue-500 text-white px-4 py-2 rounded"
          disabled={!data.officialDoc}
        >
          {t("next")}
        </button>
      </div>
    </div>
  );
}
