import React, { useEffect, useState } from "react";
import API from "../api/axios";

function PlayerRegisterInline({ onSuccess }) {
  const [fields, setFields] = React.useState({
    full_name: "",
    dob: "",
    gender: "",
    parent_name: "",
    parent_contact: "",
    parent_email: "",
  });
  const [photo, setPhoto] = React.useState(null);
  const [birthCert, setBirthCert] = React.useState(null);
  const [error, setError] = React.useState("");
  const [loading, setLoading] = React.useState(false);

  const handleChange = (e) => {
    setFields((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      const formData = new FormData();
      Object.entries(fields).forEach(([k, v]) => {
        if (v) formData.append(k, v);
      });
      if (photo) formData.append("photo", photo);
      if (birthCert) formData.append("birthCertificate", birthCert);

      await API.post("/players/register", formData);
      alert("Player registered!");
      setFields({
        full_name: "",
        dob: "",
        gender: "",
        parent_name: "",
        parent_contact: "",
        parent_email: "",
      });
      setPhoto(null);
      setBirthCert(null);
      if (onSuccess) onSuccess();
    } catch (err) {
      setError(
        err.response?.data?.message || "Registration failed, please try again."
      );
    }
    setLoading(false);
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 bg-white rounded shadow mt-6">
      <h3 className="mb-4 font-semibold">Register New Player</h3>
      <input
        name="full_name"
        placeholder="Full Name"
        value={fields.full_name}
        onChange={handleChange}
        required
        className="block mb-2 p-2 border rounded w-full"
      />
      <input
        name="dob"
        type="date"
        placeholder="Date of Birth"
        value={fields.dob}
        onChange={handleChange}
        required
        className="block mb-2 p-2 border rounded w-full"
      />
      <select
        name="gender"
        value={fields.gender}
        onChange={handleChange}
        required
        className="block mb-2 p-2 border rounded w-full"
      >
        <option value="">Select Gender</option>
        <option value="Male">Male</option>
        <option value="Female">Female</option>
        <option value="Other">Other</option>
      </select>
      <input
        name="parent_name"
        placeholder="Parent Name"
        value={fields.parent_name}
        onChange={handleChange}
        className="block mb-2 p-2 border rounded w-full"
      />
      <input
        name="parent_contact"
        placeholder="Parent Contact"
        value={fields.parent_contact}
        onChange={handleChange}
        className="block mb-2 p-2 border rounded w-full"
      />
      <input
        name="parent_email"
        placeholder="Parent Email"
        type="email"
        value={fields.parent_email}
        onChange={handleChange}
        className="block mb-2 p-2 border rounded w-full"
      />
      <label className="block mb-1">Photo (optional):</label>
      <input
        type="file"
        accept="image/*"
        onChange={(e) => setPhoto(e.target.files[0])}
      />
      <label className="block my-1">Birth Certificate (optional):</label>
      <input
        type="file"
        accept="image/*"
        onChange={(e) => setBirthCert(e.target.files[0])}
      />
      {error && <div className="text-red-600 mt-2">{error}</div>}
      <button
        type="submit"
        disabled={loading}
        className="mt-3 px-4 py-2 bg-green-600 text-white rounded"
      >
        {loading ? "Registering..." : "Register Player"}
      </button>
    </form>
  );
}

export default function SchoolDashboard() {
  const [stats, setStats] = React.useState(null);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState("");
  const [refreshPlayers, setRefreshPlayers] = React.useState(false);

  React.useEffect(() => {
    async function fetchStats() {
      try {
        const res = await API.get("/schools/me");
        setStats(res.data);
      } catch {
        setError("Failed to load dashboard stats.");
      } finally {
        setLoading(false);
      }
    }
    fetchStats();
  }, [refreshPlayers]);

  if (loading) return <div className="p-6">Loading dashboard...</div>;
  if (error) return <div className="p-6 text-red-600">{error}</div>;

  return (
    <div className="min-h-screen bg-blue-50 p-6">
      <h1 className="text-2xl font-bold mb-4">School Admin Dashboard</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div className="bg-white rounded-xl shadow p-4 flex flex-col items-center">
          <span className="text-lg font-semibold">School Name</span>
          <span>{stats.name}</span>
        </div>
        <div className="bg-white rounded-xl shadow p-4 flex flex-col items-center">
          <span className="text-lg font-semibold">Address</span>
          <span>{stats.address}</span>
        </div>
        <div className="bg-white rounded-xl shadow p-4 flex flex-col items-center">
          <span className="text-lg font-semibold">Phone</span>
          <span>{stats.phone}</span>
        </div>
      </div>

      {/* Embed player registration */}
      <PlayerRegisterInline onSuccess={() => setRefreshPlayers(!refreshPlayers)} />
    </div>
  );
}
