// src/components/Topbar.js

import React from "react";
import { FaSignOutAlt } from "react-icons/fa";

/**
 * Topbar for ATHLETIQ dashboard.
 * Shows: Welcome user, school code, onboarding status, logout button.
 * Props:
 *  - user: current user object (from localStorage)
 *  - school: current school object (from API)
 *  - onLogout: logout handler
 */
export default function Topbar({ user, school, onLogout }) {
  // Status badge color mapping
  const statusColor = {
    completed: "bg-green-100 text-green-700 border-green-400",
    pending: "bg-yellow-100 text-yellow-800 border-yellow-400",
    rejected: "bg-red-100 text-red-800 border-red-400",
  };
  const status = (school?.onboarding_status || "pending").toLowerCase();

  return (
    <header className="flex items-center justify-between h-20 px-8 bg-[#f6fbf6] border-b border-[#d0e5da]">
      <div className="flex items-center gap-6">
        {/* Welcome message */}
        <div>
          <div className="text-lg font-semibold text-[#113653]">
            Welcome, {user?.full_name || user?.email}
          </div>
          <div className="text-xs text-[#254469]">
            <span className="font-bold">School Code:</span> {school?.school_code || "..."}
          </div>
        </div>
        {/* Onboarding status badge */}
        <span
          className={
            "ml-2 px-3 py-1 rounded-full border text-xs font-bold uppercase tracking-wide " +
            (statusColor[status] || "bg-gray-200 text-gray-800 border-gray-400")
          }
        >
          {status === "completed"
            ? "✔ Onboarding Complete"
            : status === "pending"
            ? "⏳ Onboarding Pending"
            : "❌ Rejected"}
        </span>
      </div>
      <div className="flex items-center gap-4">
        {/* School logo and name (mini) */}
        {school?.logo_url && (
          <img
            src={school.logo_url}
            alt="School"
            className="w-10 h-10 rounded-full object-cover border border-[#b7c8d7]"
          />
        )}
        <span className="font-semibold text-[#113653]">{school?.name}</span>
        {/* Logout */}
        <button
          className="ml-4 flex items-center gap-2 bg-[#113653] hover:bg-[#205a8c] text-white px-4 py-2 rounded transition"
          onClick={onLogout}
        >
          <FaSignOutAlt />
          <span>Logout</span>
        </button>
      </div>
    </header>
  );
}
