import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Register() {
  const [role, setRole] = useState('school_admin');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [schoolName, setSchoolName] = useState('');
  const [err, setErr] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setErr('');
    setSuccess('');
    try {
      let url = '';
      let data = {};
      if (role === 'school_admin') {
        url = `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api/school/register'}`;
        data = { email, password, school_name: schoolName };
        const res = await axios.post(url, data);
        const schoolId = res.data.school_id;
        localStorage.setItem('school_id', schoolId);
        setSuccess('School admin registered! Please complete your school profile.');
        setTimeout(() => {
          navigate('/school-onboarding');
        }, 1000);
      } else {
        url = `${process.env.REACT_APP_API_URL || 'http://localhost:5000/api/auth/register'}`;
        data = { fullName, email, password, role };
        await axios.post(url, data);
        setSuccess('Registered! You can login now.');
        setTimeout(() => {
          navigate('/login');
        }, 1000);
      }
    } catch (error) {
      setErr(error?.response?.data?.message || 'Registration failed');
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      <form className="bg-white p-8 rounded shadow w-full max-w-md" onSubmit={handleRegister}>
        <h2 className="text-xl font-bold mb-4">Register</h2>
        <div className="mb-4">
          <label className="block mb-1">Role:</label>
          <select className="w-full border p-2" value={role} onChange={e => setRole(e.target.value)}>
            <option value="school_admin">School Admin</option>
            <option value="coach">Coach</option>
            <option value="referee">Referee</option>
            <option value="organization">Organization</option>
            <option value="super_admin">Super Admin</option>
            <option value="player">Player</option>
          </select>
        </div>
        {role === 'school_admin' && (
          <input
            className="w-full border p-2 mb-2"
            placeholder="School Name"
            value={schoolName}
            onChange={e => setSchoolName(e.target.value)}
            required
          />
        )}
        {role !== 'school_admin' && (
          <input
            className="w-full border p-2 mb-2"
            placeholder="Full Name"
            value={fullName}
            onChange={e => setFullName(e.target.value)}
            required
          />
        )}
        <input
          className="w-full border p-2 mb-2"
          placeholder="Email"
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          required
        />
        <input
          className="w-full border p-2 mb-2"
          placeholder="Password"
          type="password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          required
        />
        {err && <div className="text-red-500 mb-2">{err}</div>}
        {success && <div className="text-green-500 mb-2">{success}</div>}
        <button className="w-full py-2 bg-green-500 text-white rounded" type="submit">Register</button>
      </form>
    </div>
  );
}
